                await this.providers[i].getBlockNumber();
                logger.info(`Provider ${config.rpc.urls[i]} is healthy`);
            } catch (error) {
                logger.warn(`Provider ${config.rpc.urls[i]} is unhealthy: ${error.message}`);
            }
        }
