            } catch (error: any) {
                logger.error('Error getting address transactions:', error);
                res.status(500).json({ error: 'Failed to get transactions' });
            }
        });
    }

    start(): void {
        this.app.listen(this.port, () => {
            logger.info(`API server listening on port ${this.port}`);
        });
    }
