                VALUES ($1, $2, $3, $4)
                ON CONFLICT (token_address, token_id) DO UPDATE SET
                    owner_address = $3,
                    last_updated = $4`,
                [
                    tokenAddress.toLowerCase(),
                    tokenId,
                    ownerAddress.toLowerCase(),
