            tokenType?: 'ERC20' | 'ERC721' | 'ERC1155';
            limit?: number;
            offset?: number;
        }
    ): Promise<TokenTransfer[]> {
        try {
            const conditions = [];
            const params = [];
            let paramIndex = 1;

            if (options.tokenAddress) {
                conditions.push(`token_address = $${paramIndex++}`);
                params.push(options.tokenAddress.toLowerCase());
