                res.status(500).json({ error: 'Failed to get pending transactions' });
            }
        });

        // Filter logs
        this.app.post('/logs/filter', async (req, res) => {
            try {
                const filter = req.body as LogFilter;

                // Validate filter
                if (!filter || (!filter.address && !filter.topics)) {
