                const isContract = await blockchain.isContract(address);

                if (isContract) {
                    // Determine token type
                    const tokenType = await blockchain.getTokenType(address);

                    if (tokenType) {
                        return res.json({ 
                            address, 
                            type: 'contract', 
                            contractType: tokenType 
                        });
                    } else {
                        return res.json({ 
                            address, 
                            type: 'contract', 
                            contractType: 'unknown' 
