                                type: 'address', 
                                addressType: 'contract',
                                contractType: 'unknown',
                                data: { 
                                    address: query, 
                                    transactions 
                                } 
                            });
                        }
                    } else {
                        // It's a regular wallet address
                        return res.json({ 
                            type: 'address', 
                            addressType: 'wallet',
                            data: { 
                                address: query, 
                                transactions 
                            } 
                        });
                    }
                }

                res.status(404).json({ error: 'No results found' });
            } catch (error: any) {
                logger.error('Error searching:', error);
                res.status(500).json({ error: 'Search failed' });
            }
