        if (timeSinceLastBlock < pollInterval * 2) {
          pollInterval = Math.max(2000, pollInterval * 0.8); // Decrease interval, min 2 seconds
        } else {
          pollInterval = Math.min(15000, pollInterval * 1.2); // Increase interval, max 15 seconds
