        // Fetch and update the UI with new block data
        fetchAndUpdateBlocks(currentBlock);
      }
    } catch (error) {
      console.error('Error in adaptive polling:', error);
    }

    // Schedule next poll with the adaptive interval
    setTimeout(checkForNewBlocks, pollInterval);
  };

  // Start the polling
  checkForNewBlocks();
}
```

## Approach 3: Direct RPC Connection (for more real-time updates)

If the polling approach isn't real-time enough, you can use the RPC proxy endpoint to establish a more direct connection to the blockchain:

```javascript
async function subscribeToNewBlocks() {
  // Check if the RPC endpoint supports eth_subscribe (not all do)
  try {
    const response = await axios.post(`${BASE_URL}/proxy/rpc`, {
      jsonrpc: '2.0',
      id: 1,
      method: 'eth_subscribe',
      params: ['newHeads']
    });

    // If supported, you'd get a subscription ID
    console.log('Subscription successful:', response.data);

    // You would then need to handle the subscription events
    // This typically requires a WebSocket connection
  } catch (error) {
    console.log('Subscription not supported, falling back to polling');
    setupPolling();
  }
}
```

## Approach 4: WebSocket Implementation (if you control the backend)

If you have access to modify the indexer, you could add WebSocket support to the API service for true real-time updates:

```javascript
// Frontend code to connect to a WebSocket endpoint
const socket = new WebSocket('wss://mainnetindexer.studio-blockchain.com/ws');

socket.onopen = () => {
  console.log('WebSocket connection established');
  socket.send(JSON.stringify({ subscribe: 'newBlocks' }));
};

socket.onmessage = (event) => {
  const data = JSON.parse(event.data);
  if (data.type === 'newBlock') {
    // Update UI with the new block
    addNewBlockToUI(data.block);
  }
};

socket.onerror = (error) => {
  console.error('WebSocket error:', error);
};

socket.onclose = () => {
  console.log('WebSocket connection closed');
  // Implement reconnection logic
  setTimeout(connectWebSocket, 3000);
};
```

