            const result = await this.pool.query(
                `SELECT n.*, m.metadata 
                FROM nft_tokens n
                LEFT JOIN nft_metadata m ON n.token_address = m.token_address AND n.token_id = m.token_id
                WHERE n.token_address = $1 AND n.token_id = $2`,
