import { indexer } from './services/indexer';
import { createApiService } from './services/api';
import { blockchain } from './services/blockchain';
import { db } from './services/database/index';
import { createLogger } from './utils/logger';
import { config } from './config';

const logger = createLogger('main');

process.on('uncaughtException', (error) => {
    logger.error('Uncaught Exception:', error);
    process.exit(1);
});

process.on('unhandledRejection', (reason, promise) => {
    logger.error('Unhandled Rejection at:', promise, 'reason:', reason);
    process.exit(1);
});

async function main() {
    try {
        logger.info('Starting Studio Blockchain Indexer...');
        logger.info(`RPC URLs: ${config.rpc.urls.join(', ')}`);
        logger.info(`Chain ID: ${config.rpc.chainId}`);
        logger.info(`Starting from block: ${config.indexer.startBlock}`);

        // Create and start the API server
        const api = createApiService(db, indexer, config.server.port);
        api.start();
        logger.info(`API server started on port ${config.server.port}`);

        // Start the indexer
        await indexer.start();
        logger.info('Indexer started successfully');

        // Handle shutdown gracefully
        const shutdown = async () => {
            logger.info('Shutting down...');

            // Stop the indexer
            indexer.stop();

            // Shutdown blockchain service (stop health check interval)
            if (typeof blockchain.shutdown === 'function') {
                blockchain.shutdown();
            }

            process.exit(0);
        };

        process.on('SIGINT', shutdown);
        process.on('SIGTERM', shutdown);

    } catch (error) {
        logger.error('Fatal error:', error);
        process.exit(1);
    }
}

main().catch((error) => {
    logger.error('Startup error:', error);
    process.exit(1);
});

interface DatabaseConfig {
    host: string;
    port: number;
    database: string;
    user: string;
    password: string;
}

interface RpcConfig {
    urls: string[];
    chainId: number;
}

interface IndexerConfig {
    startBlock: number;
    batchSize: number;
    confirmations: number;
}

interface ServerConfig {
    port: number;
    host: string;
}

interface Config {
    db: DatabaseConfig;
    rpc: RpcConfig;
    indexer: IndexerConfig;
    server: ServerConfig;
}

// Define all available RPC URLs for mainnet
const MAINNET_RPC_URLS = [
    'https://mainnet.studio-blockchain.com',
    'https://mainnet2.studio-blockchain.com',
    'https://mainnet3.studio-blockchain.com',
    'https://mainnet.studio-scan.com',
    'https://mainnet2.studio-scan.com'
    // WebSocket URL is not included as it uses a different protocol
    // 'wss://mainnet.studio-blockchain.com:8547'
];

const config: Config = {
    db: {
        host: process.env.DB_HOST || 'localhost',
        port: parseInt(process.env.DB_PORT || '5432'),
        database: process.env.DB_NAME || 'studio_indexer',
        user: process.env.DB_USER || 'postgres',
        password: process.env.DB_PASSWORD || 'postgres'
    },
    rpc: {
        urls: MAINNET_RPC_URLS,
        chainId: parseInt(process.env.CHAIN_ID || '240241')
    },
    indexer: {
        startBlock: parseInt(process.env.START_BLOCK || '0'),
        batchSize: parseInt(process.env.BATCH_SIZE || '10'),
        confirmations: parseInt(process.env.CONFIRMATIONS || '12')
    },
    server: {
        port: parseInt(process.env.PORT || '3000'),
        host: process.env.HOST || '0.0.0.0'
    }
};

export { config };
export type { Config, DatabaseConfig, RpcConfig, IndexerConfig, ServerConfig };

import { blockchain } from './blockchain';
import { db } from './database/index';
import { config } from '../config';
import { createLogger } from '../utils/logger';

const logger = createLogger('indexer');

class Indexer {
    private _isRunning: boolean = false;
    private _latestProcessedBlock: number = 0;
    private _addressesToUpdate: Set<string> = new Set<string>();
    private _lastBalanceUpdateTime: number = 0;
    private _balanceUpdateInterval: number = 60 * 1000; // Update balances every 60 seconds
    private _fullBalanceUpdateInterval: number = 3600 * 1000; // Full update every hour
    private _lastFullBalanceUpdateTime: number = 0;

    // Public getters for private properties
    get isRunning(): boolean {
        return this._isRunning;
    }

    get latestProcessedBlock(): number {
        return this._latestProcessedBlock;
    }

    async start() {
        if (this._isRunning) {
            return;
        }

        this._isRunning = true;
        logger.info('Starting indexer...');

        try {
            // Get the latest block from the chain
            const latestBlockNumber = await blockchain.getLatestBlockNumber();

            // Get the latest block we've processed
            this._latestProcessedBlock = await db.getLatestBlock();

            // If we're starting fresh, use the configured start block
            if (this._latestProcessedBlock === 0) {
                this._latestProcessedBlock = config.indexer.startBlock;
            }

            // Start processing blocks
            this.processBlocks();
        } catch (error) {
            logger.error('Error starting indexer:', error);
            this._isRunning = false;
        }
    }

    private async processBlocks() {
        while (this._isRunning) {
            try {
                // Get the latest block number from the chain
                const latestBlockNumber = await blockchain.getLatestBlockNumber();

                // Don't process if we're caught up
                if (this._latestProcessedBlock >= latestBlockNumber - config.indexer.confirmations) {
                    await new Promise(resolve => setTimeout(resolve, 5000));
                    continue;
                }

                // Process the next block
                const nextBlock = this._latestProcessedBlock + 1;
                logger.info(`Processing block ${nextBlock}`);

                const { block, transactions } = await blockchain.getBlockWithTransactions(nextBlock);

                // Store the block
                await db.insertBlock(block);

                // Store each transaction and process token transfers
                for (const tx of transactions) {
                    try {
                        await db.insertTransaction(tx);

                        // Process token transfers
                        await this.processTokenTransfers(tx.hash, tx.blockNumber, block.timestamp);

                        logger.info(`Processed transaction ${tx.hash} in block ${block.number}`);
                    } catch (error) {
                        logger.error(`Error processing transaction ${tx.hash}:`, error);
                    }
                }

                // Process new contracts in this block
                await this.processNewContracts(nextBlock, nextBlock);

                this._latestProcessedBlock = nextBlock;

                // Log progress every 10 blocks
                if (nextBlock % 10 === 0) {
                    logger.info(`Processed up to block ${nextBlock}, chain head is at ${latestBlockNumber}`);
                }

            } catch (error) {
                logger.error('Error processing blocks:', error);
                // Wait before retrying
                await new Promise(resolve => setTimeout(resolve, 5000));
            }
        }
    }

    private async processTokenTransfers(txHash: string, blockNumber: number, timestamp: number) {
        try {
            // Get the transaction receipt
            const receipt = await blockchain.getTransactionReceipt(txHash);
            if (!receipt) {
                logger.warn(`Receipt not found for transaction ${txHash}`);
                return;
            }

            // Get token transfers from the receipt
            const transfers = await blockchain.getTokenTransfersFromReceipt(receipt);

            // Store each token transfer
            for (const transfer of transfers) {
                try {
                    await db.insertTokenTransfer({
                        transactionHash: txHash,
                        blockNumber,
                        tokenAddress: transfer.tokenAddress,
                        fromAddress: transfer.from,
                        toAddress: transfer.to,
                        value: transfer.value,
                        tokenType: transfer.tokenType,
                        tokenId: transfer.tokenId,
                        timestamp
                    });

                    // Add addresses involved in token transfers to the update queue
                    if (transfer.tokenType === 'ERC20') {
                        if (transfer.from !== '0x0000000000000000000000000000000000000000') {
                            this._addressesToUpdate.add(transfer.from.toLowerCase());
                        }
                        if (transfer.to !== '0x0000000000000000000000000000000000000000') {
                            this._addressesToUpdate.add(transfer.to.toLowerCase());
                        }
                    }

                    // If this is an NFT transfer, update the NFT metadata
                    if ((transfer.tokenType === 'ERC721' || transfer.tokenType === 'ERC1155') && transfer.tokenId) {
                        await this.processNFTMetadata(transfer.tokenAddress, transfer.tokenId);
                    }

                    // If this is a new token, update the token collection info
                    await this.processTokenCollection(transfer.tokenAddress);

                    logger.info(`Processed ${transfer.tokenType} transfer in transaction ${txHash}`);
                } catch (error) {
                    logger.error(`Error processing token transfer in transaction ${txHash}:`, error);
                }
            }

            // Check if it's time to update token balances
            await this.checkAndUpdateTokenBalances();
        } catch (error) {
            logger.error(`Error processing token transfers for transaction ${txHash}:`, error);
        }
    }

    /**
     * Check if it's time to update token balances and update them if needed
     */
    private async checkAndUpdateTokenBalances() {
        const now = Date.now();

        // Check if it's time for a full balance update (all addresses in the system)
        if (now - this._lastFullBalanceUpdateTime >= this._fullBalanceUpdateInterval) {
            await this.updateAllTokenBalances();
            this._lastFullBalanceUpdateTime = now;
            this._lastBalanceUpdateTime = now;
            return;
        }

        // Only update balances if enough time has passed since the last update
        if (now - this._lastBalanceUpdateTime < this._balanceUpdateInterval) {
            return;
        }

        // Update token balances for addresses that have had recent activity
        if (this._addressesToUpdate.size > 0) {
            logger.info(`Updating token balances for ${this._addressesToUpdate.size} addresses with recent activity`);

            // Get known token addresses
            const knownTokenAddresses = await blockchain.getKnownTokenAddresses();

            // Process addresses in batches to avoid overloading the blockchain provider
            const batchSize = 5;
            const addresses = Array.from(this._addressesToUpdate);

            for (let i = 0; i < addresses.length; i += batchSize) {
                const batch = addresses.slice(i, i + batchSize);

                // Process each address in the batch
                await Promise.all(batch.map(async (address: string) => {
                    try {
                        // Get token balances directly from the blockchain
                        const balances = await blockchain.getAddressTokenBalances(address, knownTokenAddresses);

                        // Update the database with the latest balances
                        for (const balance of balances) {
                            try {
                                // Directly update the token balance in the database
                                await db.updateTokenBalance(
                                    address,
                                    balance.tokenAddress,
                                    balance.balance,
                                    balance.tokenType
                                );

                                logger.info(`Updated token balance for ${address} and token ${balance.tokenAddress}: ${balance.balance}`);
                            } catch (error) {
                                logger.error(`Error updating token balance for ${address} and token ${balance.tokenAddress}:`, error);
                            }
                        }
                    } catch (error) {
                        logger.error(`Error updating token balances for ${address}:`, error);
                    }
                }));

                // Wait a bit between batches to avoid rate limiting
                if (i + batchSize < addresses.length) {
                    await new Promise(resolve => setTimeout(resolve, 1000));
                }
            }

            // Clear the addresses to update
            this._addressesToUpdate.clear();
        }

        // Update the last balance update time
        this._lastBalanceUpdateTime = now;
    }

    /**
     * Update token balances for all addresses in the system
     * This is a comprehensive update that ensures all balances are accurate
     */
    private async updateAllTokenBalances() {
        try {
            logger.info('Starting full token balance update for all addresses in the system');

            // Get all addresses with token balances from the database
            const addresses = await db.getAllAddressesWithTokenBalances();
            logger.info(`Found ${addresses.length} addresses with token balances in the database`);

            // Get known token addresses
            const knownTokenAddresses = await blockchain.getKnownTokenAddresses();
            logger.info(`Found ${knownTokenAddresses.length} known token addresses`);

            // Process addresses in batches to avoid overloading the blockchain provider
            const batchSize = 5;

            for (let i = 0; i < addresses.length; i += batchSize) {
                const batch = addresses.slice(i, i + batchSize);

                // Process each address in the batch
                await Promise.all(batch.map(async (address: string) => {
                    try {
                        // Get token balances directly from the blockchain
                        const balances = await blockchain.getAddressTokenBalances(address, knownTokenAddresses);

                        // Update the database with the latest balances
                        for (const balance of balances) {
                            try {
                                // Directly update the token balance in the database
                                await db.updateTokenBalance(
                                    address,
                                    balance.tokenAddress,
                                    balance.balance,
                                    balance.tokenType
                                );

                                logger.info(`Updated token balance for ${address} and token ${balance.tokenAddress}: ${balance.balance}`);
                            } catch (error) {
                                logger.error(`Error updating token balance for ${address} and token ${balance.tokenAddress}:`, error);
                            }
                        }
                    } catch (error) {
                        logger.error(`Error updating token balances for ${address}:`, error);
                    }
                }));

                // Wait a bit between batches to avoid rate limiting
                if (i + batchSize < addresses.length) {
                    await new Promise(resolve => setTimeout(resolve, 1000));
                }

                // Log progress
                if (i % 50 === 0 && i > 0) {
                    logger.info(`Processed ${i}/${addresses.length} addresses (${Math.round(i / addresses.length * 100)}%)`);
                }
            }

            logger.info('Completed full token balance update for all addresses');
        } catch (error) {
            logger.error('Error updating all token balances:', error);
        }
    }

    private async processNFTMetadata(tokenAddress: string, tokenId: string) {
        try {
            // Check if we already have metadata for this token
            const existingMetadata = await db.getNFTMetadata(tokenAddress, tokenId);
            if (existingMetadata) {
                // We already have metadata for this token
                return;
            }

            // Get the token URI
            const tokenURI = await blockchain.getTokenURI(tokenAddress, tokenId);
            if (!tokenURI) {
                logger.warn(`Token URI not found for token ${tokenAddress} with ID ${tokenId}`);
                return;
            }

            // Get the token metadata
            const metadata = await blockchain.getTokenMetadata(tokenAddress, tokenId);
            if (!metadata) {
                logger.warn(`Metadata not found for token ${tokenAddress} with ID ${tokenId}`);
                return;
            }

            // Store the metadata
            await db.updateNFTMetadata(tokenAddress, tokenId, metadata);

            logger.info(`Processed metadata for token ${tokenAddress} with ID ${tokenId}`);
        } catch (error) {
            logger.error(`Error processing NFT metadata for token ${tokenAddress} with ID ${tokenId}:`, error);
        }
    }

    private async processTokenCollection(tokenAddress: string) {
        try {
            // Check if we already have collection info for this token
            const existingCollection = await db.getNFTCollection(tokenAddress);
            if (existingCollection) {
                // We already have collection info for this token
                return;
            }

            // Get the token type
            const tokenType = await blockchain.getTokenType(tokenAddress);
            if (!tokenType || tokenType === 'ERC20') {
                // We only care about NFT collections
                return;
            }

            // Get the token name and symbol
            const name = await blockchain.getTokenName(tokenAddress);
            const symbol = await blockchain.getTokenSymbol(tokenAddress);

            // Get the token total supply
            const totalSupply = await blockchain.getTokenTotalSupply(tokenAddress);

            // Store the collection info
            await db.updateNFTCollection(
                tokenAddress,
                name || 'Unknown',
                symbol || 'UNKNOWN',
                totalSupply ? totalSupply.toNumber() : undefined
            );

            logger.info(`Processed collection info for token ${tokenAddress}`);
        } catch (error) {
            logger.error(`Error processing token collection for token ${tokenAddress}:`, error);
        }
    }

    private async processNewContracts(fromBlock: number, toBlock: number) {
        try {
            logger.info(`Detecting new contracts from block ${fromBlock} to ${toBlock}`);

            // Use the blockchain service to detect new contracts
            const contracts = await blockchain.detectNewContracts(fromBlock, toBlock);

            // Store each contract in the database
            for (const contract of contracts) {
                try {
                    await db.storeContract(contract);
                    logger.info(`Stored contract ${contract.address} in the database`);

                    // If this is a token contract, update token information
                    if (contract.contractType && contract.contractType !== 'UNKNOWN') {
                        // Process token information
                        if (contract.contractType === 'ERC20') {
                            // For ERC20 tokens, update token balances
                            logger.info(`Processing ERC20 token ${contract.address}`);
                            // This would be handled by the token transfers processing
                        } else if (contract.contractType === 'ERC721' || contract.contractType === 'ERC1155') {
                            // For NFT tokens, update collection information
                            logger.info(`Processing NFT collection ${contract.address}`);
                            await this.processTokenCollection(contract.address);
                        }
                    }
                } catch (error) {
                    logger.error(`Error storing contract ${contract.address}:`, error);
                }
            }

            logger.info(`Processed ${contracts.length} new contracts from block ${fromBlock} to ${toBlock}`);
        } catch (error) {
            logger.error(`Error processing new contracts from block ${fromBlock} to ${toBlock}:`, error);
        }
    }

    stop() {
        this._isRunning = false;
        logger.info('Stopping indexer...');
    }
}

export const indexer = new Indexer();

import { blockchainCore } from './core';
import { tokenService } from './tokens';
import * as types from './types';

// Re-export everything from the blockchain service
export * from './types';
export * from './abis';

// Create a combined blockchain service that includes both core and token functionality
class BlockchainService {
    // Core blockchain methods
    getLatestBlockNumber = blockchainCore.getLatestBlockNumber.bind(blockchainCore);
    getBlock = blockchainCore.getBlock.bind(blockchainCore);
    getBlockWithTransactions = blockchainCore.getBlockWithTransactions.bind(blockchainCore);
    getTransaction = blockchainCore.getTransaction.bind(blockchainCore);
    getTransactionReceipt = blockchainCore.getTransactionReceipt.bind(blockchainCore);
    getCode = blockchainCore.getCode.bind(blockchainCore);
    getBalance = blockchainCore.getBalance.bind(blockchainCore);
    isContract = blockchainCore.isContract.bind(blockchainCore);
    getContractCreationInfo = blockchainCore.getContractCreationInfo.bind(blockchainCore);
    getLogs = blockchainCore.getLogs.bind(blockchainCore);
    getPendingTransactions = blockchainCore.getPendingTransactions.bind(blockchainCore);
    callContractMethod = blockchainCore.callContractMethod.bind(blockchainCore);
    getProvider = blockchainCore.getProvider.bind(blockchainCore);
    shutdown = blockchainCore.shutdown.bind(blockchainCore);

    // Token methods
    getTokenTransfersFromReceipt = tokenService.getTokenTransfersFromReceipt.bind(tokenService);
    getTokenType = tokenService.getTokenType.bind(tokenService);
    getTokenName = tokenService.getTokenName.bind(tokenService);
    getTokenSymbol = tokenService.getTokenSymbol.bind(tokenService);
    getTokenDecimals = tokenService.getTokenDecimals.bind(tokenService);
    getTokenTotalSupply = tokenService.getTokenTotalSupply.bind(tokenService);
    getTokenURI = tokenService.getTokenURI.bind(tokenService);
    getTokenMetadata = tokenService.getTokenMetadata.bind(tokenService);
    getTokenBalance = tokenService.getTokenBalance.bind(tokenService);
    getTokenHolders = tokenService.getTokenHolders.bind(tokenService);
    getContractOwner = tokenService.getContractOwner.bind(tokenService);
    detectNewContracts = tokenService.detectNewContracts.bind(tokenService);
    getAddressTokenBalances = tokenService.getAddressTokenBalances.bind(tokenService);
    getKnownTokenAddresses = tokenService.getKnownTokenAddresses.bind(tokenService);
    updateTokenBalancesForAddress = tokenService.updateTokenBalancesForAddress.bind(tokenService);
}

export const blockchain = new BlockchainService();

import { ethers } from 'ethers';
import { config } from '../../config';
import { createLogger } from '../../utils/logger';
import { BlockData, TransactionData, LogFilterOptions } from './types';

const logger = createLogger('blockchain:core');

class BlockchainCore {
    private provider: ethers.providers.JsonRpcProvider;
    private currentRpcIndex: number = 0;
    private healthCheckInterval: NodeJS.Timeout | null = null;

    constructor() {
        this.provider = new ethers.providers.JsonRpcProvider(config.rpc.urls[0]);
        this.setupHealthCheck();
    }

    private setupHealthCheck() {
        // Check RPC health every 30 seconds
        this.healthCheckInterval = setInterval(async () => {
            try {
                await this.provider.getBlockNumber();
            } catch (error) {
                logger.error('RPC health check failed, switching to next RPC URL', error);
                this.switchRpcProvider();
            }
        }, 30000);
    }

    private switchRpcProvider() {
        this.currentRpcIndex = (this.currentRpcIndex + 1) % config.rpc.urls.length;
        const newRpcUrl = config.rpc.urls[this.currentRpcIndex];
        logger.info(`Switching to RPC URL: ${newRpcUrl}`);
        this.provider = new ethers.providers.JsonRpcProvider(newRpcUrl);
    }

    async getLatestBlockNumber(): Promise<number> {
        try {
            return await this.provider.getBlockNumber();
        } catch (error) {
            logger.error('Error getting latest block number:', error);
            this.switchRpcProvider();
            throw error;
        }
    }

    async getBlock(blockNumber: number): Promise<BlockData> {
        try {
            const block = await this.provider.getBlock(blockNumber);
            if (!block) {
                throw new Error(`Block ${blockNumber} not found`);
            }
            return {
                number: block.number,
                hash: block.hash,
                parentHash: block.parentHash,
                timestamp: block.timestamp,
                nonce: block.nonce || '0x0',
                difficulty: ethers.BigNumber.from(block.difficulty),
                gasLimit: ethers.BigNumber.from(block.gasLimit),
                gasUsed: ethers.BigNumber.from(block.gasUsed),
                miner: block.miner,
                extraData: block.extraData,
                transactions: block.transactions
            };
        } catch (error) {
            logger.error(`Error getting block ${blockNumber}:`, error);
            this.switchRpcProvider();
            throw error;
        }
    }

    async getBlockWithTransactions(blockNumber: number): Promise<{ block: BlockData, transactions: TransactionData[] }> {
        try {
            const block = await this.provider.getBlockWithTransactions(blockNumber);
            if (!block) {
                throw new Error(`Block ${blockNumber} not found`);
            }

            const blockData: BlockData = {
                number: block.number,
                hash: block.hash,
                parentHash: block.parentHash,
                timestamp: block.timestamp,
                nonce: block.nonce || '0x0',
                difficulty: ethers.BigNumber.from(block.difficulty),
                gasLimit: ethers.BigNumber.from(block.gasLimit),
                gasUsed: ethers.BigNumber.from(block.gasUsed),
                miner: block.miner,
                extraData: block.extraData,
                transactions: block.transactions.map(tx => tx.hash)
            };

            const transactions: TransactionData[] = block.transactions.map(tx => ({
                hash: tx.hash,
                blockNumber: tx.blockNumber || block.number,
                from: tx.from,
                to: tx.to || null,
                value: tx.value,
                gasPrice: tx.gasPrice || ethers.BigNumber.from(0),
                gasLimit: tx.gasLimit,
                data: tx.data,
                nonce: tx.nonce,
                transactionIndex: tx.confirmations ? tx.confirmations - 1 : 0, // Use confirmations as a fallback
                timestamp: block.timestamp
            }));

            return { block: blockData, transactions };
        } catch (error) {
            logger.error(`Error getting block with transactions ${blockNumber}:`, error);
            this.switchRpcProvider();
            throw error;
        }
    }

    async getTransaction(txHash: string): Promise<TransactionData> {
        try {
            const tx = await this.provider.getTransaction(txHash);
            if (!tx) {
                throw new Error(`Transaction ${txHash} not found`);
            }

            const receipt = await this.provider.getTransactionReceipt(txHash);
            const block = await this.provider.getBlock(tx.blockNumber || 0);

            return {
                hash: tx.hash,
                blockNumber: tx.blockNumber || 0,
                from: tx.from,
                to: tx.to || null,
                value: tx.value,
                gasPrice: tx.gasPrice || ethers.BigNumber.from(0),
                gasLimit: tx.gasLimit,
                data: tx.data,
                nonce: tx.nonce,
                transactionIndex: receipt ? receipt.transactionIndex : 0,
                status: receipt ? receipt.status === 1 : undefined,
                timestamp: block ? block.timestamp : 0
            };
        } catch (error) {
            logger.error(`Error getting transaction ${txHash}:`, error);
            this.switchRpcProvider();
            throw error;
        }
    }

    async getTransactionReceipt(txHash: string): Promise<ethers.providers.TransactionReceipt | null> {
        try {
            return await this.provider.getTransactionReceipt(txHash);
        } catch (error) {
            logger.error(`Error getting transaction receipt ${txHash}:`, error);
            this.switchRpcProvider();
            throw error;
        }
    }

    async getCode(address: string): Promise<string> {
        try {
            return await this.provider.getCode(address);
        } catch (error) {
            logger.error(`Error getting code for address ${address}:`, error);
            this.switchRpcProvider();
            throw error;
        }
    }

    async getBalance(address: string): Promise<ethers.BigNumber> {
        try {
            return await this.provider.getBalance(address);
        } catch (error) {
            logger.error(`Error getting balance for address ${address}:`, error);
            this.switchRpcProvider();
            throw error;
        }
    }

    async isContract(address: string): Promise<boolean> {
        try {
            const code = await this.getCode(address);
            return code !== '0x';
        } catch (error) {
            logger.error(`Error checking if address ${address} is contract:`, error);
            this.switchRpcProvider();
            throw error;
        }
    }

    async getContractCreationInfo(address: string): Promise<import('./types').ContractCreationInfo | null> {
        try {
            // First, check if the address is a contract
            const isContract = await this.isContract(address);
            if (!isContract) {
                return null;
            }

            // Get the transaction history for the address
            // This is a simplified approach - in a production environment,
            // you would use a more efficient method like querying an indexer
            // or using a specialized API

            // For now, we'll use a simple approach to find the contract creation transaction
            // by looking for the first transaction to the address with empty input data

            // Get the latest block number
            const latestBlock = await this.getLatestBlockNumber();

            // Start from a reasonable block range (e.g., last 10000 blocks)
            // In a production environment, you would use a more efficient method
            const startBlock = Math.max(0, latestBlock - 10000);

            // Create a filter to find transactions to the contract address
            const filter = {
                fromBlock: startBlock,
                toBlock: 'latest',
                address: address
            };

            // Get logs for the contract address
            const logs = await this.getLogs(filter);

            if (logs.length === 0) {
                return null;
            }

            // Sort logs by block number (ascending)
            logs.sort((a, b) => a.blockNumber - b.blockNumber);

            // Get the first log's transaction
            const firstLog = logs[0];
            const tx = await this.getTransaction(firstLog.transactionHash);

            // Get the block for timestamp
            const block = await this.getBlock(firstLog.blockNumber);

            return {
                creator: tx.from,
                transactionHash: tx.hash,
                blockNumber: tx.blockNumber,
                timestamp: block.timestamp
            };
        } catch (error) {
            logger.error(`Error getting contract creation info for ${address}:`, error);
            this.switchRpcProvider();
            return null;
        }
    }

    async getLogs(filter: LogFilterOptions): Promise<ethers.providers.Log[]> {
        try {
            // Convert our LogFilterOptions to the format expected by ethers
            const ethersFilter: ethers.providers.Filter = {
                fromBlock: filter.fromBlock,
                toBlock: filter.toBlock,
                topics: filter.topics
            };

            // Handle address separately since ethers expects a different type
            if (filter.address) {
                if (Array.isArray(filter.address)) {
                    // If it's an array, we need to handle it differently
                    // For simplicity, we'll just use the first address
                    // In a real implementation, you might want to make multiple requests
                    ethersFilter.address = filter.address[0];
                } else {
                    ethersFilter.address = filter.address;
                }
            }

            return await this.provider.getLogs(ethersFilter);
        } catch (error) {
            logger.error('Error getting logs:', error);
            this.switchRpcProvider();
            throw error;
        }
    }

    async getPendingTransactions(): Promise<TransactionData[]> {
        try {
            const pendingTxs = await this.provider.send('eth_pendingTransactions', []);
            return pendingTxs.map((tx: any) => ({
                hash: tx.hash,
                blockNumber: tx.blockNumber ? parseInt(tx.blockNumber, 16) : 0,
                from: tx.from,
                to: tx.to || null,
                value: ethers.BigNumber.from(tx.value),
                gasPrice: ethers.BigNumber.from(tx.gasPrice),
                gasLimit: ethers.BigNumber.from(tx.gas),
                data: tx.input,
                nonce: parseInt(tx.nonce, 16),
                transactionIndex: 0,
                timestamp: Math.floor(Date.now() / 1000)
            }));
        } catch (error) {
            logger.error('Error getting pending transactions:', error);
            this.switchRpcProvider();
            return [];
        }
    }

    async callContractMethod(
        contractAddress: string,
        abi: string[] | ethers.ContractInterface,
        methodName: string,
        args: any[]
    ): Promise<any> {
        try {
            const contract = new ethers.Contract(contractAddress, abi, this.provider);
            return await contract[methodName](...args);
        } catch (error) {
            throw error;
        }
    }

    shutdown() {
        if (this.healthCheckInterval) {
            clearInterval(this.healthCheckInterval);
            this.healthCheckInterval = null;
        }
    }

    getProvider(): ethers.providers.JsonRpcProvider {
        return this.provider;
    }
}

export const blockchainCore = new BlockchainCore();

import { ethers } from 'ethers';
import axios from 'axios';
import { createLogger } from '../../utils/logger';
import { blockchainCore } from './core';
import { 
    ERC20_ABI, 
    ERC721_ABI, 
    ERC1155_ABI, 
    OWNABLE_ABI, 
    CONTRACT_CREATION_TOPIC 
} from './abis';
import { 
    TokenTransferData, 
    ContractData, 
    TokenBalance 
} from './types';

const logger = createLogger('blockchain:tokens');

class TokenService {
    async getTokenTransfersFromReceipt(receipt: ethers.providers.TransactionReceipt): Promise<TokenTransferData[]> {
        const transfers: TokenTransferData[] = [];

        try {
            // Check for ERC20 Transfer events
            const erc20Interface = new ethers.utils.Interface(ERC20_ABI);
            const erc20TransferTopic = erc20Interface.getEventTopic('Transfer');

            // Check for ERC721 Transfer events
            const erc721Interface = new ethers.utils.Interface(ERC721_ABI);
            const erc721TransferTopic = erc721Interface.getEventTopic('Transfer');

            // Check for ERC1155 TransferSingle events
            const erc1155Interface = new ethers.utils.Interface(ERC1155_ABI);
            const erc1155SingleTransferTopic = erc1155Interface.getEventTopic('TransferSingle');
            const erc1155BatchTransferTopic = erc1155Interface.getEventTopic('TransferBatch');

            for (const log of receipt.logs) {
                try {
                    if (log.topics[0] === erc20TransferTopic && log.topics.length === 3) {
                        // ERC20 Transfer
                        const parsedLog = erc20Interface.parseLog(log);
                        transfers.push({
                            tokenAddress: log.address,
                            from: ethers.utils.getAddress('0x' + log.topics[1].substring(26)),
                            to: ethers.utils.getAddress('0x' + log.topics[2].substring(26)),
                            value: parsedLog.args.value.toString(),
                            tokenType: 'ERC20'
                        });
                    } else if (log.topics[0] === erc721TransferTopic && log.topics.length === 4) {
                        // ERC721 Transfer
                        const tokenId = ethers.BigNumber.from(log.topics[3]).toString();
                        transfers.push({
                            tokenAddress: log.address,
                            from: ethers.utils.getAddress('0x' + log.topics[1].substring(26)),
                            to: ethers.utils.getAddress('0x' + log.topics[2].substring(26)),
                            value: '1',
                            tokenType: 'ERC721',
                            tokenId
                        });
                    } else if (log.topics[0] === erc1155SingleTransferTopic) {
                        // ERC1155 TransferSingle
                        const parsedLog = erc1155Interface.parseLog(log);
                        transfers.push({
                            tokenAddress: log.address,
                            from: parsedLog.args.from,
                            to: parsedLog.args.to,
                            value: parsedLog.args.value.toString(),
                            tokenType: 'ERC1155',
                            tokenId: parsedLog.args.id.toString()
                        });
                    } else if (log.topics[0] === erc1155BatchTransferTopic) {
                        // ERC1155 TransferBatch
                        try {
                            const parsedLog = erc1155Interface.parseLog(log);

                            // Skip if we don't have the expected arguments
                            if (!parsedLog.args || !parsedLog.args.from || !parsedLog.args.to) {
                                continue;
                            }

                            // Extract ids and values safely
                            const extractArrayData = (data: any): any[] => {
                                if (!data) return [];
                                if (Array.isArray(data)) return data;
                                return [data]; // Convert single item to array
                            };

                            const ids = extractArrayData(parsedLog.args.ids);
                            const values = extractArrayData(parsedLog.args.values);

                            // Process each id/value pair if we have both arrays
                            const length = Math.min(ids.length, values.length);
                            for (let i = 0; i < length; i++) {
                                transfers.push({
                                    tokenAddress: log.address,
                                    from: parsedLog.args.from,
                                    to: parsedLog.args.to,
                                    value: String(values[i]),
                                    tokenType: 'ERC1155',
                                    tokenId: String(ids[i])
                                });
                            }
                        } catch (batchError) {
                            logger.error(`Error processing ERC1155 batch transfer in transaction ${receipt.transactionHash}:`, batchError);
                        }
                    }
                } catch (error) {
                    logger.error(`Error parsing log in transaction ${receipt.transactionHash}:`, error);
                    continue;
                }
            }
        } catch (error) {
            logger.error(`Error getting token transfers from receipt ${receipt.transactionHash}:`, error);
        }

        return transfers;
    }

    async getTokenType(tokenAddress: string): Promise<'ERC20' | 'ERC721' | 'ERC1155' | null> {
        try {
            // Check if it's a contract
            const isContract = await blockchainCore.isContract(tokenAddress);
            if (!isContract) {
                return null;
            }

            // Try ERC721 supportsInterface
            try {
                const erc721Interface = '0x80ac58cd'; // ERC721 interface id
                const erc1155Interface = '0xd9b67a26'; // ERC1155 interface id

                // Check for ERC165 support
                const supportsERC165 = await blockchainCore.callContractMethod(
                    tokenAddress,
                    ['function supportsInterface(bytes4 interfaceId) view returns (bool)'],
                    'supportsInterface',
                    ['0x01ffc9a7'] // ERC165 interface id
                );

                if (supportsERC165) {
                    // Check for ERC721 support
                    const supportsERC721 = await blockchainCore.callContractMethod(
                        tokenAddress,
                        ['function supportsInterface(bytes4 interfaceId) view returns (bool)'],
                        'supportsInterface',
                        [erc721Interface]
                    );

                    if (supportsERC721) {
                        return 'ERC721';
                    }

                    // Check for ERC1155 support
                    const supportsERC1155 = await blockchainCore.callContractMethod(
                        tokenAddress,
                        ['function supportsInterface(bytes4 interfaceId) view returns (bool)'],
                        'supportsInterface',
                        [erc1155Interface]
                    );

                    if (supportsERC1155) {
                        return 'ERC1155';
                    }
                }
            } catch (error) {
                // Ignore errors, continue with other checks
            }

            // Try ERC20 methods
            try {
                // Check for basic ERC20 methods
                const [name, symbol, decimals, totalSupply] = await Promise.all([
                    blockchainCore.callContractMethod(tokenAddress, ERC20_ABI, 'name', []),
                    blockchainCore.callContractMethod(tokenAddress, ERC20_ABI, 'symbol', []),
                    blockchainCore.callContractMethod(tokenAddress, ERC20_ABI, 'decimals', []),
                    blockchainCore.callContractMethod(tokenAddress, ERC20_ABI, 'totalSupply', [])
                ]);

                // If we got here without errors, it's likely an ERC20
                return 'ERC20';
            } catch (error) {
                // Not an ERC20
            }

            // Try ERC721 methods
            try {
                // Check for basic ERC721 methods
                const [name, symbol, tokenURI] = await Promise.all([
                    blockchainCore.callContractMethod(tokenAddress, ERC721_ABI, 'name', []),
                    blockchainCore.callContractMethod(tokenAddress, ERC721_ABI, 'symbol', []),
                    blockchainCore.callContractMethod(tokenAddress, ERC721_ABI, 'tokenURI', [0])
                ]);

                // If we got here without errors, it's likely an ERC721
                return 'ERC721';
            } catch (error) {
                // Not an ERC721
            }

            // Try ERC1155 methods
            try {
                // Check for basic ERC1155 methods
                const uri = await blockchainCore.callContractMethod(tokenAddress, ERC1155_ABI, 'uri', [0]);

                // If we got here without errors, it's likely an ERC1155
                return 'ERC1155';
            } catch (error) {
                // Not an ERC1155
            }

            return null;
        } catch (error) {
            logger.error(`Error determining token type for ${tokenAddress}:`, error);
            return null;
        }
    }

    async getTokenName(tokenAddress: string): Promise<string | null> {
        try {
            return await blockchainCore.callContractMethod(tokenAddress, ERC20_ABI, 'name', []);
        } catch (error) {
            logger.error(`Error getting token name for ${tokenAddress}:`, error);
            return null;
        }
    }

    async getTokenSymbol(tokenAddress: string): Promise<string | null> {
        try {
            return await blockchainCore.callContractMethod(tokenAddress, ERC20_ABI, 'symbol', []);
        } catch (error) {
            logger.error(`Error getting token symbol for ${tokenAddress}:`, error);
            return null;
        }
    }

    async getTokenDecimals(tokenAddress: string): Promise<number | null> {
        try {
            const decimals = await blockchainCore.callContractMethod(tokenAddress, ERC20_ABI, 'decimals', []);
            return parseInt(decimals);
        } catch (error) {
            logger.error(`Error getting token decimals for ${tokenAddress}:`, error);
            return null;
        }
    }

    async getTokenTotalSupply(tokenAddress: string, tokenId?: string): Promise<ethers.BigNumber | null> {
        try {
            const tokenType = await this.getTokenType(tokenAddress);

            if (tokenType === 'ERC20') {
                return await blockchainCore.callContractMethod(tokenAddress, ERC20_ABI, 'totalSupply', []);
            } else if (tokenType === 'ERC721') {
                return await blockchainCore.callContractMethod(tokenAddress, ERC721_ABI, 'totalSupply', []);
            } else if (tokenType === 'ERC1155' && tokenId) {
                return await blockchainCore.callContractMethod(tokenAddress, ERC1155_ABI, 'totalSupply', [tokenId]);
            }

            return null;
        } catch (error) {
            logger.error(`Error getting token total supply for ${tokenAddress}:`, error);
            return null;
        }
    }

    async getTokenURI(tokenAddress: string, tokenId: string): Promise<string | null> {
        try {
            const tokenType = await this.getTokenType(tokenAddress);

            if (tokenType === 'ERC721') {
                return await blockchainCore.callContractMethod(tokenAddress, ERC721_ABI, 'tokenURI', [tokenId]);
            } else if (tokenType === 'ERC1155') {
                return await blockchainCore.callContractMethod(tokenAddress, ERC1155_ABI, 'uri', [tokenId]);
            }

            return null;
        } catch (error) {
            logger.error(`Error getting token URI for ${tokenAddress} with ID ${tokenId}:`, error);
            return null;
        }
    }

    async getTokenMetadata(tokenAddress: string, tokenId: string): Promise<any | null> {
        try {
            const tokenURI = await this.getTokenURI(tokenAddress, tokenId);
            if (!tokenURI) {
                return null;
            }

            // Handle IPFS URIs
            let uri = tokenURI;
            if (uri.startsWith('ipfs://')) {
                uri = uri.replace('ipfs://', 'https://ipfs.io/ipfs/');
            }

            // Fetch metadata
            const response = await axios.get(uri, { timeout: 5000 });
            return response.data;
        } catch (error) {
            logger.error(`Error getting token metadata for ${tokenAddress} with ID ${tokenId}:`, error);
            return null;
        }
    }

    async getTokenBalance(tokenAddress: string, ownerAddress: string, tokenId?: string): Promise<ethers.BigNumber | null> {
        try {
            const tokenType = await this.getTokenType(tokenAddress);

            if (tokenType === 'ERC20') {
                return await blockchainCore.callContractMethod(tokenAddress, ERC20_ABI, 'balanceOf', [ownerAddress]);
            } else if (tokenType === 'ERC721') {
                return await blockchainCore.callContractMethod(tokenAddress, ERC721_ABI, 'balanceOf', [ownerAddress]);
            } else if (tokenType === 'ERC1155' && tokenId) {
                return await blockchainCore.callContractMethod(tokenAddress, ERC1155_ABI, 'balanceOf', [ownerAddress, tokenId]);
            }

            return null;
        } catch (error) {
            logger.error(`Error getting token balance for ${tokenAddress} owned by ${ownerAddress}:`, error);
            return null;
        }
    }

    async getTokenHolders(tokenAddress: string): Promise<{ address: string, balance: string }[] | null> {
        try {
            // This is a complex operation that would require indexing all Transfer events
            // For now, we'll return null as this would be implemented in the indexer
            return null;
        } catch (error) {
            logger.error(`Error getting token holders for ${tokenAddress}:`, error);
            return null;
        }
    }

    async getContractOwner(contractAddress: string): Promise<string | null> {
        try {
            // Try to call the owner() method from Ownable
            return await blockchainCore.callContractMethod(contractAddress, OWNABLE_ABI, 'owner', []);
        } catch (error) {
            logger.error(`Error getting contract owner for ${contractAddress}:`, error);
            return null;
        }
    }

    async detectNewContracts(fromBlock: number, toBlock: number): Promise<ContractData[]> {
        try {
            logger.info(`Detecting new contracts from block ${fromBlock} to ${toBlock}`);
            const contracts: ContractData[] = [];

            // Get all blocks in the range
            for (let blockNumber = fromBlock; blockNumber <= toBlock; blockNumber++) {
                try {
                    const { block, transactions } = await blockchainCore.getBlockWithTransactions(blockNumber);

                    // Check each transaction for contract creation
                    for (const tx of transactions) {
                        // Contract creation transactions have null 'to' address
                        if (tx.to === null) {
                            try {
                                const receipt = await blockchainCore.getTransactionReceipt(tx.hash);
                                if (receipt && receipt.contractAddress) {
                                    logger.info(`Detected new contract at ${receipt.contractAddress} in transaction ${tx.hash}`);

                                    // Get contract details
                                    const contractData: ContractData = {
                                        address: receipt.contractAddress,
                                        creatorAddress: tx.from,
                                        blockNumber: tx.blockNumber,
                                        timestamp: tx.timestamp,
                                        transactionHash: tx.hash,
                                        balance: (await blockchainCore.getBalance(receipt.contractAddress)).toString()
                                    };

                                    // Get contract type and details
                                    const contractType = await this.getTokenType(receipt.contractAddress);
                                    if (contractType) {
                                        contractData.contractType = contractType;
                                        contractData.name = await this.getTokenName(receipt.contractAddress) || undefined;
                                        contractData.symbol = await this.getTokenSymbol(receipt.contractAddress) || undefined;

                                        if (contractType === 'ERC20') {
                                            const decimals = await this.getTokenDecimals(receipt.contractAddress);
                                            if (decimals !== null) {
                                                contractData.decimals = decimals;
                                            }

                                            const totalSupply = await this.getTokenTotalSupply(receipt.contractAddress);
                                            if (totalSupply) {
                                                contractData.totalSupply = totalSupply.toString();
                                            }
                                        }
                                    } else {
                                        contractData.contractType = 'UNKNOWN';
                                    }

                                    // Get contract owner
                                    const owner = await this.getContractOwner(receipt.contractAddress);
                                    if (owner) {
                                        contractData.ownerAddress = owner;
                                    }

                                    // Get token balances
                                    const tokenBalances = await this.getAddressTokenBalances(receipt.contractAddress);
                                    if (tokenBalances && tokenBalances.length > 0) {
                                        contractData.tokenBalances = tokenBalances;
                                    }

                                    contracts.push(contractData);
                                }
                            } catch (error) {
                                logger.error(`Error processing contract creation transaction ${tx.hash}:`, error);
                            }
                        }
                    }
                } catch (error) {
                    logger.error(`Error processing block ${blockNumber}:`, error);
                }
            }

            return contracts;
        } catch (error) {
            logger.error(`Error detecting new contracts from block ${fromBlock} to ${toBlock}:`, error);
            return [];
        }
    }

    /**
     * Get all token balances for an address by directly querying the blockchain
     * This is the most reliable way to get token balances
     */
    async getAddressTokenBalances(address: string, knownTokenAddresses?: string[]): Promise<TokenBalance[]> {
        try {
            const balances: TokenBalance[] = [];
            const tokenAddressesToCheck = new Set<string>();

            // If known token addresses are provided, use them
            if (knownTokenAddresses && knownTokenAddresses.length > 0) {
                for (const tokenAddress of knownTokenAddresses) {
                    tokenAddressesToCheck.add(tokenAddress.toLowerCase());
                }
                logger.info(`Checking ${tokenAddressesToCheck.size} known token addresses for ${address}`);
            } else {
                // Otherwise, find tokens the address has interacted with
                logger.info(`No known token addresses provided, finding tokens ${address} has interacted with`);

                // Check for transfers TO this address
                const logsTo = await blockchainCore.getLogs({
                    fromBlock: 0,
                    toBlock: 'latest',
                    topics: [
                        // Transfer(address,address,uint256)
                        '0xddf252ad1be2c89b69c2b068fc378daa952ba7f163c4a11628f55a4df523b3ef',
                        null,
                        // Filter for transfers to this address
                        '0x000000000000000000000000' + address.substring(2).toLowerCase()
                    ]
                });

                // Check for transfers FROM this address
                const logsFrom = await blockchainCore.getLogs({
                    fromBlock: 0,
                    toBlock: 'latest',
                    topics: [
                        // Transfer(address,address,uint256)
                        '0xddf252ad1be2c89b69c2b068fc378daa952ba7f163c4a11628f55a4df523b3ef',
                        // Filter for transfers from this address
                        '0x000000000000000000000000' + address.substring(2).toLowerCase(),
                        null
                    ]
                });

                // Combine the logs and extract unique token addresses
                const logs = [...logsTo, ...logsFrom];
                for (const log of logs) {
                    tokenAddressesToCheck.add(log.address.toLowerCase());
                }

                logger.info(`Found ${tokenAddressesToCheck.size} unique token addresses for ${address} from blockchain logs`);
            }

            // For each token, directly query its balance
            for (const tokenAddress of tokenAddressesToCheck) {
                try {
                    // Check if it's an ERC20 token
                    const tokenType = await this.getTokenType(tokenAddress);
                    if (tokenType !== 'ERC20') continue;

                    // Get the token balance directly from the contract
                    const balance = await this.getTokenBalance(tokenAddress, address);
                    if (!balance) continue;

                    // Get token metadata
                    const [name, symbol, decimals] = await Promise.all([
                        this.getTokenName(tokenAddress),
                        this.getTokenSymbol(tokenAddress),
                        this.getTokenDecimals(tokenAddress)
                    ]);

                    balances.push({
                        tokenAddress: tokenAddress,
                        tokenType: 'ERC20',
                        balance: balance.toString(),
                        name: name || undefined,
                        symbol: symbol || undefined,
                        decimals: decimals || undefined
                    });

                    logger.info(`Retrieved balance for token ${tokenAddress} (${symbol || 'Unknown'}): ${balance.toString()}`);
                } catch (error) {
                    logger.error(`Error getting token balance for ${tokenAddress}:`, error);
                }
            }

            logger.info(`Found ${balances.length} token balances for address ${address} from blockchain`);
            return balances;
        } catch (error) {
            logger.error(`Error getting token balances for address ${address}:`, error);
            return [];
        }
    }

    /**
     * Get all known token addresses from the blockchain
     * This can be used to maintain a list of known tokens
     */
    async getKnownTokenAddresses(): Promise<string[]> {
        try {
            // This would typically come from a database of known tokens
            // For now, we'll return a hardcoded list of common tokens on this chain
            return [
                '0xfccc20bf4f0829e121bc99ff2222456ad4465a1e', // USDT
                // Add other known tokens here
            ];
        } catch (error) {
            logger.error('Error getting known token addresses:', error);
            return [];
        }
    }

    /**
     * Update token balances for an address in the database
     * This should be called periodically to keep the database in sync with the blockchain
     */
    async updateTokenBalancesForAddress(address: string): Promise<TokenBalance[]> {
        try {
            // Get known token addresses
            const knownTokenAddresses = await this.getKnownTokenAddresses();

            // Get token balances directly from the blockchain
            const balances = await this.getAddressTokenBalances(address, knownTokenAddresses);

            logger.info(`Updated ${balances.length} token balances for ${address}`);
            return balances;
        } catch (error) {
            logger.error(`Error updating token balances for ${address}:`, error);
            return [];
        }
    }
}

export const tokenService = new TokenService();

import { ethers } from 'ethers';
import { Database } from './core';
import { BlockchainDatabase } from './blockchain';
import { TokensDatabase } from './tokens';
import { NFTsDatabase } from './nfts';
import { LogsDatabase } from './logs';
import { ContractsDatabase } from './contracts';
import { IDatabase } from './types';

// Re-export all types
export * from './types';

/**
 * Combined database class that extends all the database classes
 * This class combines all the functionality from the different database classes
 */
class CombinedDatabase extends BlockchainDatabase implements IDatabase {
    private tokensDb: TokensDatabase;
    private nftsDb: NFTsDatabase;
    private logsDb: LogsDatabase;
    private contractsDb: ContractsDatabase;

    constructor() {
        super();
        this.tokensDb = new TokensDatabase();
        this.nftsDb = new NFTsDatabase();
        this.logsDb = new LogsDatabase();
        this.contractsDb = new ContractsDatabase();
    }

    // Override token methods to use the TokensDatabase
    async insertTokenTransfer(transfer: import('./types').TokenTransfer): Promise<void> {
        return this.tokensDb.insertTokenTransfer(transfer);
    }

    async getTokenTransfers(options: import('./types').TokenTransferOptions): Promise<import('./types').TokenTransfer[]> {
        return this.tokensDb.getTokenTransfers(options);
    }

    async getAddressTokenTransfers(address: string, options?: import('./types').AddressTokenTransferOptions): Promise<import('./types').TokenTransfer[]> {
        return this.tokensDb.getAddressTokenTransfers(address, options);
    }

    async getAddressTokenBalances(address: string, options?: import('./types').TokenBalanceOptions): Promise<import('./types').TokenBalance[]> {
        return this.tokensDb.getAddressTokenBalances(address, options);
    }

    async getTokenHolders(tokenAddress: string, limit?: number, offset?: number): Promise<{ address: string, balance: string, percentage?: number }[]> {
        return this.tokensDb.getTokenHolders(tokenAddress, limit, offset);
    }

    async getTokenHoldersCount(tokenAddress: string): Promise<number> {
        return this.tokensDb.getTokenHoldersCount(tokenAddress);
    }

    async getTokenTransfersCount(tokenAddress: string): Promise<number> {
        return this.tokensDb.getTokenTransfersCount(tokenAddress);
    }

    async getAllAddressesWithTokenBalances(): Promise<string[]> {
        return this.tokensDb.getAllAddressesWithTokenBalances();
    }

    async updateTokenBalance(
        address: string,
        tokenAddress: string,
        balance: string,
        tokenType: string,
        tokenId?: string
    ): Promise<void> {
        return this.tokensDb.updateTokenBalance(address, tokenAddress, balance, tokenType, tokenId);
    }

    // Override NFT methods to use the NFTsDatabase
    async updateNFTMetadata(tokenAddress: string, tokenId: string, metadata: any): Promise<void> {
        return this.nftsDb.updateNFTMetadata(tokenAddress, tokenId, metadata);
    }

    async updateNFTCollection(tokenAddress: string, name: string, symbol: string, totalSupply?: number): Promise<void> {
        return this.nftsDb.updateNFTCollection(tokenAddress, name, symbol, totalSupply);
    }

    async getNFTsByOwner(ownerAddress: string, options?: import('./types').NFTsByOwnerOptions): Promise<import('./types').NFTToken[]> {
