const app = express();
app.use(express.json());

app.post('/verify', async (req, res) => {
  const { sourceCode, compilerVersion, optimizer, runs, address } = req.body;

  try {
    // Step 1: Compile
    const compiled = await compileSource(sourceCode, compilerVersion, { optimize: optimizer, runs });

    // Step 2: Compare bytecode
    const isVerified = await verifyContract(address, compiled.evm.bytecode.object);

    if (isVerified) {
      // Save to database
      await saveVerifiedContract(address, sourceCode, compilerVersion, optimizer, runs, compiled.abi);
      res.json({ status: 'verified' });
    } else {
      res.status(400).json({ error: 'Bytecode mismatch' });
    }
  } catch (err) {
    res.status(500).json({ error: err.message });
  }
