        }
        throw error;
    }
}

async function interactWithContract() {
    try {
        console.log('Interacting with contract...');

        const response = await axios.post(`${API_URL}/contracts/${CONTRACT_ADDRESS}/interact`, {
            method: 'getValue',
            params: []
        });

        console.log('Interaction result:');
