async function getValidatorPayout(validatorAddress) {
  const blocks = await getBlocksByMiner(validatorAddress);
  let totalPayoutWei = web3.utils.toBN(0);

  for (const block of blocks) {
    const revenue = await calculateBlockRevenue(block);
    totalPayoutWei = totalPayoutWei.add(revenue);
  }

  // Convert to ether (or STO, adjust decimals if needed)
  const totalPayoutEther = web3.utils.fromWei(totalPayoutWei, 'ether');

  return {
    wei: totalPayoutWei.toString(),
    ether: totalPayoutEther,
