                    if (supportsERC721) {
                        return 'ERC721';
                    }

                    // Check for ERC1155 support
                    const supportsERC1155 = await blockchainCore.callContractMethod(
                        tokenAddress,
                        ['function supportsInterface(bytes4 interfaceId) view returns (bool)'],
                        'supportsInterface',
                        [erc1155Interface]
                    );

