            if (tokenType === 'ERC721') {
                return await blockchainCore.callContractMethod(tokenAddress, ERC721_ABI, 'tokenURI', [tokenId]);
            } else if (tokenType === 'ERC1155') {
                return await blockchainCore.callContractMethod(tokenAddress, ERC1155_ABI, 'uri', [tokenId]);
            }

