                        const tokenId = ethers.BigNumber.from(log.topics[3]).toString();
                        transfers.push({
                            tokenAddress: log.address,
                            from: ethers.utils.getAddress('0x' + log.topics[1].substring(26)),
                            to: ethers.utils.getAddress('0x' + log.topics[2].substring(26)),
