import express from 'express';
import { IDatabase } from '../database/types';
import { createLogger } from '../../utils/logger';
import { blockchain } from '../blockchain';
import { formatResponse, handleError } from './utils';

const logger = createLogger('api:contracts');

/**
 * API service for contract-related endpoints
 */
export class ContractsApiService {
    private database: IDatabase;
    private indexer: any;
    private app: express.Application;

    constructor(database: IDatabase, indexer: any, port: number, app: express.Application) {
        this.database = database;
        this.indexer = indexer;
        this.app = app;

        this.setupRoutes();
    }

    private setupRoutes() {
        // Get contract details
        this.app.get('/contracts/:address', this.getContract.bind(this));

        // Get contracts by creator
        this.app.get('/address/:address/contracts', this.getContractsByCreator.bind(this));

        // Get token contracts
        this.app.get('/tokens', this.getTokenContracts.bind(this));

        // Get contract creation info
        this.app.get('/contracts/:address/creation', this.getContractCreationInfo.bind(this));

        // Get account balances (tokens and native)
        this.app.get('/account/:address/balances', this.getAccountBalances.bind(this));

        // Get address tokens
        this.app.get('/address/:address/tokens', this.getAddressTokens.bind(this));

        // NOTE: Removed duplicate token-transfers endpoint to avoid conflict with TokensApiService
        // The endpoint is already registered in TokensApiService

        // Get token information
        this.app.get('/tokens/:tokenAddress', this.getTokenInfo.bind(this));

        // Get token holders
        this.app.get('/tokens/:tokenAddress/holders', this.getTokenHolders.bind(this));

        // Contract verification endpoints
        this.app.post('/contracts/verify', this.verifyContract.bind(this));
        this.app.get('/contracts/:address/verified', this.isContractVerified.bind(this));
        this.app.get('/contracts/:address/abi', this.getContractABI.bind(this));
        this.app.get('/contracts/:address/source', this.getContractSource.bind(this));
        this.app.post('/contracts/:address/interact', this.interactWithContract.bind(this));
    }

    /**
     * Get contract details
     */
    private async getContract(req: express.Request, res: express.Response) {
        try {
            const address = req.params.address.toLowerCase();

            // Get contract from database
            const contract = await this.database.getContract(address);

            if (!contract) {
                // If not in database, check if it's a contract on-chain
                const isContract = await blockchain.isContract(address);

                if (!isContract) {
                    return formatResponse(res, { error: 'Not a contract address' }, 404);
                }

                // Get contract creation info
                const creationInfo = await blockchain.getContractCreationInfo(address);

                if (!creationInfo) {
                    return formatResponse(res, { error: 'Contract creation info not found' }, 404);
                }

                // Get contract type and details
                const tokenType = await blockchain.getTokenType(address);
                const nameResult = tokenType ? await blockchain.getTokenName(address) : null;
                const symbolResult = tokenType ? await blockchain.getTokenSymbol(address) : null;
                let decimalsResult;
                let totalSupply;

                if (tokenType === 'ERC20') {
                    decimalsResult = await blockchain.getTokenDecimals(address);
                    totalSupply = await blockchain.getTokenTotalSupply(address);
                }

                // Convert null values to undefined
                const name = nameResult || undefined;
                const symbol = symbolResult || undefined;
                const decimals = decimalsResult || undefined;

                // Create contract data
                const contractData: import('../blockchain/types').ContractData = {
                    address,
                    creatorAddress: creationInfo.creator,
                    blockNumber: creationInfo.blockNumber,
                    timestamp: creationInfo.timestamp,
                    transactionHash: creationInfo.transactionHash,
                    contractType: (tokenType as 'ERC20' | 'ERC721' | 'ERC1155' | 'UNKNOWN') || 'UNKNOWN',
                    name,
                    symbol,
                    decimals,
                    totalSupply: totalSupply ? totalSupply.toString() : undefined
                };

                // Store contract in database
                await this.database.storeContract(contractData);

                return formatResponse(res, contractData);
            }

            return formatResponse(res, contract);
        } catch (error) {
            return handleError(res, error, 'Error getting contract');
        }
    }

    /**
     * Get contracts created by an address
     */
    private async getContractsByCreator(req: express.Request, res: express.Response) {
        try {
            const address = req.params.address.toLowerCase();
            const limit = parseInt(req.query.limit as string) || 10;
            const offset = parseInt(req.query.offset as string) || 0;

            const contracts = await this.database.getContractsByCreator(address, limit, offset);

            return formatResponse(res, contracts);
        } catch (error) {
            return handleError(res, error, 'Error getting contracts by creator');
        }
    }

    /**
     * Get token contracts
     */
    private async getTokenContracts(req: express.Request, res: express.Response) {
        try {
            const tokenType = req.query.type as 'ERC20' | 'ERC721' | 'ERC1155' | undefined;
            const limit = parseInt(req.query.limit as string) || 10;
            const offset = parseInt(req.query.offset as string) || 0;

            const contracts = await this.database.getTokenContracts(tokenType, limit, offset);

            return formatResponse(res, contracts);
        } catch (error) {
            return handleError(res, error, 'Error getting token contracts');
        }
    }

    /**
     * Get contract creation info
     */
    private async getContractCreationInfo(req: express.Request, res: express.Response) {
        try {
            const address = req.params.address.toLowerCase();

            // Check if it's a contract
            const isContract = await blockchain.isContract(address);

            if (!isContract) {
                return formatResponse(res, { error: 'Not a contract address' }, 404);
            }

            // Get contract creation info
            const creationInfo = await blockchain.getContractCreationInfo(address);

            if (!creationInfo) {
                return formatResponse(res, { error: 'Contract creation info not found' }, 404);
            }

            return formatResponse(res, creationInfo);
        } catch (error) {
            return handleError(res, error, 'Error getting contract creation info');
        }
    }

    /**
     * Get account balances (tokens and native)
     */
    private async getAccountBalances(req: express.Request, res: express.Response) {
        try {
            const address = req.params.address.toLowerCase();

            // Get native balance
            const nativeBalance = await blockchain.getBalance(address);

            // Get token balances
            const tokenBalances = await this.database.getAddressTokenBalances(address);

            // Format token balances
            const formattedTokens = await Promise.all(
                tokenBalances.map(async (balance) => {
                    try {
                        const tokenAddress = balance.tokenAddress;
                        const tokenType = balance.tokenType;

                        // Get token details
                        const name = await blockchain.getTokenName(tokenAddress);
                        const symbol = await blockchain.getTokenSymbol(tokenAddress);
                        let decimals = 18; // Default to 18 decimals

                        if (tokenType === 'ERC20') {
                            const tokenDecimals = await blockchain.getTokenDecimals(tokenAddress);
                            if (tokenDecimals !== null) {
                                decimals = tokenDecimals;
                            }
                        }

                        // Convert balance from wei to token units
                        const balanceValue = parseFloat(balance.balance) / Math.pow(10, decimals);

                        return {
                            contractAddress: tokenAddress,
                            symbol: symbol || 'UNKNOWN',
                            name: name || 'Unknown Token',
                            balance: balanceValue,
                            decimals,
                            type: tokenType
                        };
                    } catch (error) {
                        logger.error(`Error formatting token balance for ${balance.tokenAddress}:`, error);
                        return null;
                    }
                })
            );

            // Filter out null values
            const tokens = formattedTokens.filter(token => token !== null);

            // Convert native balance from wei to ether
            const nativeValue = parseFloat(nativeBalance.toString()) / 1e18;

            return formatResponse(res, {
                native: nativeValue,
                tokens
            });
        } catch (error) {
            return handleError(res, error, 'Error getting account balances');
        }
    }

    /**
     * Get address tokens
     */
    private async getAddressTokens(req: express.Request, res: express.Response) {
        try {
            const address = req.params.address.toLowerCase();

            // Get token balances
            const tokenBalances = await this.database.getAddressTokenBalances(address);

            // Format token balances
            const formattedTokens = await Promise.all(
                tokenBalances.map(async (balance) => {
                    try {
                        const tokenAddress = balance.tokenAddress;
                        const tokenType = balance.tokenType;

                        // Get token details
                        const name = await blockchain.getTokenName(tokenAddress);
                        const symbol = await blockchain.getTokenSymbol(tokenAddress);
                        let decimals = 18; // Default to 18 decimals

                        if (tokenType === 'ERC20') {
                            const tokenDecimals = await blockchain.getTokenDecimals(tokenAddress);
                            if (tokenDecimals !== null) {
                                decimals = tokenDecimals;
                            }
                        }

                        // Convert balance from wei to token units
                        const balanceValue = parseFloat(balance.balance) / Math.pow(10, decimals);

                        return {
                            contractAddress: tokenAddress,
                            symbol: symbol || 'UNKNOWN',
                            name: name || 'Unknown Token',
                            balance: balanceValue,
                            decimals,
                            type: tokenType
                        };
                    } catch (error) {
                        logger.error(`Error formatting token balance for ${balance.tokenAddress}:`, error);
                        return null;
                    }
                })
            );

            // Filter out null values
            const tokens = formattedTokens.filter(token => token !== null);

            return formatResponse(res, tokens);
        } catch (error) {
            return handleError(res, error, 'Error getting address tokens');
        }
    }

    /**
     * Get token transfers for an address
     * NOTE: This method is kept for reference but not registered as an endpoint
     * to avoid conflict with TokensApiService
     */
    private async getAddressTokenTransfers(req: express.Request, res: express.Response) {
        try {
            const address = req.params.address.toLowerCase();
            const limit = parseInt(req.query.limit as string) || 10;
            const offset = parseInt(req.query.offset as string) || 0;

            // Get token transfers
            const transfers = await this.database.getAddressTokenTransfers(address, {
                limit,
                offset
            });

            // Format token transfers
            const formattedTransfers = await Promise.all(
                transfers.map(async (transfer) => {
                    try {
                        const tokenAddress = transfer.tokenAddress;

                        // Get token details
                        const name = await blockchain.getTokenName(tokenAddress);
                        const symbol = await blockchain.getTokenSymbol(tokenAddress);
                        let decimals = 18; // Default to 18 decimals

                        if (transfer.tokenType === 'ERC20') {
                            const tokenDecimals = await blockchain.getTokenDecimals(tokenAddress);
                            if (tokenDecimals !== null) {
                                decimals = tokenDecimals;
                            }
                        }

                        return {
                            hash: transfer.transactionHash,
                            blockNumber: transfer.blockNumber,
                            timestamp: transfer.timestamp,
                            from: transfer.fromAddress,
                            to: transfer.toAddress,
                            tokenAddress,
                            tokenSymbol: symbol || 'UNKNOWN',
                            tokenName: name || 'Unknown Token',
                            value: transfer.value,
                            decimals
                        };
                    } catch (error) {
                        logger.error(`Error formatting token transfer for ${transfer.transactionHash}:`, error);
                        return null;
                    }
                })
            );

            // Filter out null values
            const result = formattedTransfers.filter(transfer => transfer !== null);

            return formatResponse(res, result);
        } catch (error) {
            return handleError(res, error, 'Error getting token transfers');
        }
    }

    /**
     * Get token information
     */
    private async getTokenInfo(req: express.Request, res: express.Response) {
        try {
            const tokenAddress = req.params.tokenAddress.toLowerCase();

            // Check if it's a contract
            const isContract = await blockchain.isContract(tokenAddress);

            if (!isContract) {
                return formatResponse(res, { error: 'Not a contract address' }, 404);
            }

            // Get token type
            const tokenType = await blockchain.getTokenType(tokenAddress);

            if (!tokenType) {
                return formatResponse(res, { error: 'Not a token contract' }, 404);
            }

            // Get token details
            const name = await blockchain.getTokenName(tokenAddress);
            const symbol = await blockchain.getTokenSymbol(tokenAddress);
            let decimals;
            let totalSupply;

            if (tokenType === 'ERC20') {
                decimals = await blockchain.getTokenDecimals(tokenAddress);
                totalSupply = await blockchain.getTokenTotalSupply(tokenAddress);
            }

            // Get token stats from database
            const holderCount = 0; // This would require additional indexing
            const transferCount = 0; // This would require additional indexing

            return formatResponse(res, {
                address: tokenAddress,
                symbol: symbol || 'UNKNOWN',
                name: name || 'Unknown Token',
                decimals: decimals || 18,
                totalSupply: totalSupply ? totalSupply.toString() : '0',
                type: tokenType,
                holders: holderCount,
                transfers: transferCount
            });
        } catch (error) {
            return handleError(res, error, 'Error getting token info');
        }
    }

    /**
     * Get token holders
     */
    private async getTokenHolders(req: express.Request, res: express.Response) {
        try {
            const tokenAddress = req.params.tokenAddress.toLowerCase();

            // Check if it's a contract
            const isContract = await blockchain.isContract(tokenAddress);

            if (!isContract) {
                return formatResponse(res, { error: 'Not a contract address' }, 404);
            }

            // Get token type
            const tokenType = await blockchain.getTokenType(tokenAddress);

            if (!tokenType) {
                return formatResponse(res, { error: 'Not a token contract' }, 404);
            }

            // This would require additional indexing to track all token holders
            // For now, return an empty array
            return formatResponse(res, []);
        } catch (error) {
            return handleError(res, error, 'Error getting token holders');
        }
    }

    /**
     * Verify a contract by submitting its source code
     */
    private async verifyContract(req: express.Request, res: express.Response) {
        try {
            const { address, sourceCode, compilerVersion, optimizationUsed, runs, constructorArguments, contractName, libraries, evmVersion } = req.body;

            if (!address || !sourceCode || !compilerVersion || !contractName) {
                return formatResponse(res, { 
                    success: false,
                    error: 'Missing required parameters' 
                }, 400);
            }

            // Check if it's a contract
            const isContract = await blockchain.isContract(address);

            if (!isContract) {
                return formatResponse(res, { 
                    success: false,
                    error: 'Not a contract address' 
                }, 404);
            }

            // Get the contract from the database
            const contract = await this.database.getContract(address);

            if (!contract) {
                return formatResponse(res, { 
                    success: false,
                    error: 'Contract not found in database' 
                }, 404);
            }

            // Get the contract bytecode from the blockchain
            const onChainBytecode = await blockchain.getCode(address);

            // Import the verification service
            const { verificationService } = await import('../verification');

            // Validate constructor arguments
            if (constructorArguments && !verificationService.validateConstructorArguments(constructorArguments)) {
                return formatResponse(res, {
                    success: false,
                    error: 'Invalid constructor arguments. Must be a valid hex string.'
                }, 400);
            }

            // Verify the contract
            const verificationResult = await verificationService.verifyContract(
                sourceCode,
                compilerVersion,
                contractName,
                onChainBytecode,
                optimizationUsed,
                runs,
                constructorArguments,
                libraries,
                evmVersion
            );

            if (!verificationResult.success) {
                return formatResponse(res, {
                    success: false,
                    error: verificationResult.message
                }, 400);
            }

            // Extract metadata hash from bytecode
            const metadataHash = verificationService.extractMetadataHash(onChainBytecode);

            // Update the contract with verification data
            await this.database.updateContractVerification(
                address,
                true,
                sourceCode,
                verificationResult.abi,
                compilerVersion,
                optimizationUsed,
                runs,
                constructorArguments,
                libraries,
                evmVersion
            );

            return formatResponse(res, {
                success: true,
                message: 'Contract verified successfully',
                address,
                abi: verificationResult.abi,
                metadata: verificationResult.metadata,
                metadataHash
            });
        } catch (error) {
            return handleError(res, error, 'Error verifying contract');
        }
    }

    /**
     * Check if a contract is verified
     */
    private async isContractVerified(req: express.Request, res: express.Response) {
        try {
            const address = req.params.address.toLowerCase();

            // Check if it's a contract
            const isContract = await blockchain.isContract(address);

            if (!isContract) {
                return formatResponse(res, { 
                    verified: false,
                    error: 'Not a contract address' 
                });
            }

            // Get the contract verification data
            const verification = await this.database.getContractVerification(address);

            // Return whether the contract is verified and has ABI and source code
            const isVerified = verification && verification.verified && verification.abi && verification.sourceCode;

            return formatResponse(res, {
                verified: isVerified ? true : false
            });
        } catch (error) {
            return handleError(res, error, 'Error checking if contract is verified');
        }
    }

    /**
     * Get the ABI of a verified contract
     */
    private async getContractABI(req: express.Request, res: express.Response) {
        try {
            const address = req.params.address.toLowerCase();

            // Check if it's a contract
            const isContract = await blockchain.isContract(address);

            if (!isContract) {
                return formatResponse(res, { error: 'Not a contract address' }, 404);
            }

            // Get the contract from the database
            const contract = await this.database.getContract(address);

            if (!contract) {
                return formatResponse(res, { error: 'Contract not found in database' }, 404);
            }

            // Get the contract verification data
            const verification = await this.database.getContractVerification(address);

            // If the contract is verified and has an ABI, return it
            if (verification && verification.verified && verification.abi) {
                return formatResponse(res, {
                    address,
                    abi: verification.abi
                });
            }

            // If the contract is not verified, return an error
            return formatResponse(res, { 
                success: false,
                error: 'Contract is not verified' 
            }, 404);
        } catch (error) {
            return handleError(res, error, 'Error getting contract ABI');
        }
    }

    /**
     * Get the source code of a verified contract
     */
    private async getContractSource(req: express.Request, res: express.Response) {
        try {
            const address = req.params.address.toLowerCase();

            // Check if it's a contract
            const isContract = await blockchain.isContract(address);

            if (!isContract) {
                return formatResponse(res, { error: 'Not a contract address' }, 404);
            }

            // Get the contract from the database
            const contract = await this.database.getContract(address);

            if (!contract) {
                return formatResponse(res, { error: 'Contract not found in database' }, 404);
            }

            // Get the contract verification data
            const verification = await this.database.getContractVerification(address);

            // If the contract is verified and has source code, return it
            if (verification && verification.verified && verification.sourceCode) {
                return formatResponse(res, {
                    address,
                    sourceCode: verification.sourceCode
                });
            }

            // If the contract is not verified, return an error
            return formatResponse(res, { 
                success: false,
                error: 'Contract is not verified' 
            }, 404);
        } catch (error) {
            return handleError(res, error, 'Error getting contract source code');
        }
    }

    /**
     * Interact with a verified contract
     */
    private async interactWithContract(req: express.Request, res: express.Response) {
        try {
            const address = req.params.address.toLowerCase();
            const { method, params, value } = req.body;

            if (!method) {
                return formatResponse(res, { 
                    success: false,
                    error: 'Missing required parameters' 
                }, 400);
            }

            // Check if it's a contract
            const isContract = await blockchain.isContract(address);

            if (!isContract) {
                return formatResponse(res, { 
                    success: false,
                    error: 'Not a contract address' 
                }, 404);
            }

            // Get the contract from the database
            const contract = await this.database.getContract(address);

            if (!contract) {
                return formatResponse(res, { 
                    success: false,
                    error: 'Contract not found in database' 
                }, 404);
            }

            // Get the contract verification data
            const verification = await this.database.getContractVerification(address);

            // Check if the contract is verified
            if (!verification || !verification.verified) {
                return formatResponse(res, { 
                    success: false,
                    error: 'Contract is not verified' 
                }, 400);
            }

            // Check if the contract has an ABI
            if (!verification.abi) {
                return formatResponse(res, { 
                    success: false,
                    error: 'Contract does not have an ABI' 
                }, 400);
            }

            try {
                // Import ethers
                const { ethers } = await import('ethers');

                // Create a contract instance using the ABI from the verification data
                const contractInstance = new ethers.Contract(
                    address,
                    verification.abi,
                    blockchain.getProvider()
                );

                // Check if the method exists in the ABI
                const methodAbi = verification.abi.find((item: any) => 
                    item.name === method && 
                    (item.type === 'function' || item.type === undefined)
                );

                if (!methodAbi) {
                    return formatResponse(res, { 
                        success: false,
                        error: `Method ${method} not found in contract ABI` 
                    }, 400);
                }

                // Determine if the method is a read or write operation
                const isReadOperation = methodAbi.constant || 
                                       methodAbi.stateMutability === 'view' || 
                                       methodAbi.stateMutability === 'pure';

                let result;

                if (isReadOperation) {
                    // Call the method (read operation)
                    result = await contractInstance[method](...(params || []));
                } else {
                    // For write operations, we would need a private key to sign the transaction
                    // Since we don't have that in this implementation, we'll return an error
                    return formatResponse(res, { 
                        success: false,
                        error: 'Write operations are not supported in this implementation' 
                    }, 400);
                }

                return formatResponse(res, {
                    success: true,
                    address,
                    method,
                    params: params || [],
                    result
                });
            } catch (error: any) {
                return formatResponse(res, { 
                    success: false,
                    error: `Error calling contract method: ${error.message}` 
                }, 400);
            }
        } catch (error) {
            return handleError(res, error, 'Error interacting with contract');
        }
    }
}

import express from 'express';
import { ApiService } from './core';
import { BlockchainApiService } from './blockchain';
import { TokensApiService } from './tokens';
import { NFTsApiService } from './nfts';
import { ContractsApiService } from './contracts';
import { ContractsVerificationApiService } from './contracts-verification';
import { TransactionsApiService } from './transactions';
import { StatsApiService } from './stats';
import { IDatabase } from '../database/types';
import { createLogger } from '../../utils/logger';

const logger = createLogger('api:index');

/**
 * Create an API service instance
 * @param database The database instance
 * @param indexer The indexer instance
 * @param port The port to listen on
 * @returns The API service instance
 */
export function createApiService(database: IDatabase, indexer: any, port: number): ApiService {
    logger.info(`Creating combined API service on port ${port}`);

    // Create the main API service
    const apiService = new ApiService(database, indexer, port);

    // Create the specialized API services and pass the Express app from the main service
    logger.info('Registering blockchain routes');
    new BlockchainApiService(database, indexer, port, apiService['app']);

    logger.info('Registering token routes');
    new TokensApiService(database, indexer, port, apiService['app']);

    logger.info('Registering NFT routes');
    new NFTsApiService(database, indexer, port, apiService['app']);

    logger.info('Registering contract routes');
    new ContractsApiService(database, indexer, port, apiService['app']);

    logger.info('Registering contract verification routes');
    new ContractsVerificationApiService(database, apiService['app']);

    logger.info('Registering transaction routes');
    new TransactionsApiService(database, apiService['app']);

    logger.info('Registering stats routes');
    new StatsApiService(database, indexer, port, apiService['app']);

    // Log that all routes have been registered
    logger.info('All API routes registered successfully');

    return apiService;
}

// Re-export all API service classes
export { ApiService } from './core';
export { BlockchainApiService } from './blockchain';
export { TokensApiService } from './tokens';
export { NFTsApiService } from './nfts';
export { ContractsApiService } from './contracts';
export { ContractsVerificationApiService } from './contracts-verification';
export { TransactionsApiService } from './transactions';
export { StatsApiService } from './stats';

// Re-export utility functions
export * from './utils';

import { blockchain } from './blockchain';
import { db } from './database/index';
import { config } from '../config';
import { createLogger } from '../utils/logger';

const logger = createLogger('indexer');

class Indexer {
    private _isRunning: boolean = false;
    private _latestProcessedBlock: number = 0;
    private _addressesToUpdate: Set<string> = new Set<string>();
    private _lastBalanceUpdateTime: number = 0;
    private _balanceUpdateInterval: number = 60 * 1000; // Update balances every 60 seconds
    private _fullBalanceUpdateInterval: number = 3600 * 1000; // Full update every hour
    private _lastFullBalanceUpdateTime: number = 0;

    // Public getters for private properties
    get isRunning(): boolean {
        return this._isRunning;
    }

    get latestProcessedBlock(): number {
        return this._latestProcessedBlock;
    }

    async start() {
        if (this._isRunning) {
            return;
        }

        this._isRunning = true;
        logger.info('Starting indexer...');

        try {
            // Get the latest block from the chain
            const latestBlockNumber = await blockchain.getLatestBlockNumber();

            // Get the latest block we've processed
            this._latestProcessedBlock = await db.getLatestBlock();

            // If we're starting fresh, use the configured start block
            if (this._latestProcessedBlock === 0) {
                this._latestProcessedBlock = config.indexer.startBlock;
            }

            // Start processing blocks
            this.processBlocks();
        } catch (error) {
            logger.error('Error starting indexer:', error);
            this._isRunning = false;
        }
    }

    private async processBlocks() {
        while (this._isRunning) {
            try {
                // Get the latest block number from the chain
                const latestBlockNumber = await blockchain.getLatestBlockNumber();

                // Don't process if we're caught up
                if (this._latestProcessedBlock >= latestBlockNumber - config.indexer.confirmations) {
                    await new Promise(resolve => setTimeout(resolve, 5000));
                    continue;
                }

                // Process the next block
                const nextBlock = this._latestProcessedBlock + 1;
                logger.info(`Processing block ${nextBlock}`);

                const { block, transactions } = await blockchain.getBlockWithTransactions(nextBlock);

                // Store the block
                await db.insertBlock(block);

                // Store each transaction and process token transfers
                for (const tx of transactions) {
                    try {
                        await db.insertTransaction(tx);

                        // Process token transfers
                        await this.processTokenTransfers(tx.hash, tx.blockNumber, block.timestamp);

                        logger.info(`Processed transaction ${tx.hash} in block ${block.number}`);
                    } catch (error) {
                        logger.error(`Error processing transaction ${tx.hash}:`, error);
                    }
                }

                // Process new contracts in this block
                await this.processNewContracts(nextBlock, nextBlock);

                this._latestProcessedBlock = nextBlock;

                // Log progress every 10 blocks
                if (nextBlock % 10 === 0) {
                    logger.info(`Processed up to block ${nextBlock}, chain head is at ${latestBlockNumber}`);
                }

            } catch (error) {
                logger.error('Error processing blocks:', error);
                // Wait before retrying
                await new Promise(resolve => setTimeout(resolve, 5000));
            }
        }
    }

    private async processTokenTransfers(txHash: string, blockNumber: number, timestamp: number) {
        try {
            // Get the transaction receipt
            const receipt = await blockchain.getTransactionReceipt(txHash);
            if (!receipt) {
                logger.warn(`Receipt not found for transaction ${txHash}`);
                return;
            }

            // Get token transfers from the receipt
            const transfers = await blockchain.getTokenTransfersFromReceipt(receipt);

            // Store each token transfer
            for (const transfer of transfers) {
                try {
                    await db.insertTokenTransfer({
                        transactionHash: txHash,
                        blockNumber,
                        tokenAddress: transfer.tokenAddress,
                        fromAddress: transfer.from,
                        toAddress: transfer.to,
                        value: transfer.value,
                        tokenType: transfer.tokenType,
                        tokenId: transfer.tokenId,
                        timestamp
                    });

                    // Add addresses involved in token transfers to the update queue
                    if (transfer.tokenType === 'ERC20') {
                        if (transfer.from !== '0x0000000000000000000000000000000000000000') {
                            this._addressesToUpdate.add(transfer.from.toLowerCase());
                        }
                        if (transfer.to !== '0x0000000000000000000000000000000000000000') {
                            this._addressesToUpdate.add(transfer.to.toLowerCase());
                        }
                    }

                    // If this is an NFT transfer, update the NFT metadata
                    if ((transfer.tokenType === 'ERC721' || transfer.tokenType === 'ERC1155') && transfer.tokenId) {
                        await this.processNFTMetadata(transfer.tokenAddress, transfer.tokenId);
                    }

                    // If this is a new token, update the token collection info
                    await this.processTokenCollection(transfer.tokenAddress);

                    logger.info(`Processed ${transfer.tokenType} transfer in transaction ${txHash}`);
                } catch (error) {
                    logger.error(`Error processing token transfer in transaction ${txHash}:`, error);
                }
            }

            // Check if it's time to update token balances
            await this.checkAndUpdateTokenBalances();
        } catch (error) {
            logger.error(`Error processing token transfers for transaction ${txHash}:`, error);
        }
    }

    /**
     * Check if it's time to update token balances and update them if needed
     */
    private async checkAndUpdateTokenBalances() {
        const now = Date.now();

        // Check if it's time for a full balance update (all addresses in the system)
        if (now - this._lastFullBalanceUpdateTime >= this._fullBalanceUpdateInterval) {
            await this.updateAllTokenBalances();
            this._lastFullBalanceUpdateTime = now;
            this._lastBalanceUpdateTime = now;
            return;
        }

        // Only update balances if enough time has passed since the last update
        if (now - this._lastBalanceUpdateTime < this._balanceUpdateInterval) {
            return;
        }

        // Update token balances for addresses that have had recent activity
        if (this._addressesToUpdate.size > 0) {
            logger.info(`Updating token balances for ${this._addressesToUpdate.size} addresses with recent activity`);

            // Get known token addresses
            const knownTokenAddresses = await blockchain.getKnownTokenAddresses();

            // Process addresses in batches to avoid overloading the blockchain provider
            const batchSize = 5;
            const addresses = Array.from(this._addressesToUpdate);

            for (let i = 0; i < addresses.length; i += batchSize) {
                const batch = addresses.slice(i, i + batchSize);

                // Process each address in the batch
                await Promise.all(batch.map(async (address: string) => {
                    try {
                        // Get token balances directly from the blockchain
                        const balances = await blockchain.getAddressTokenBalances(address, knownTokenAddresses);

                        // Update the database with the latest balances
                        for (const balance of balances) {
                            try {
                                // Directly update the token balance in the database
                                await db.updateTokenBalance(
                                    address,
                                    balance.tokenAddress,
                                    balance.balance,
                                    balance.tokenType
                                );

                                logger.info(`Updated token balance for ${address} and token ${balance.tokenAddress}: ${balance.balance}`);
                            } catch (error) {
                                logger.error(`Error updating token balance for ${address} and token ${balance.tokenAddress}:`, error);
                            }
                        }
                    } catch (error) {
                        logger.error(`Error updating token balances for ${address}:`, error);
                    }
                }));

                // Wait a bit between batches to avoid rate limiting
                if (i + batchSize < addresses.length) {
                    await new Promise(resolve => setTimeout(resolve, 1000));
                }
            }

            // Clear the addresses to update
            this._addressesToUpdate.clear();
        }

        // Update the last balance update time
        this._lastBalanceUpdateTime = now;
    }

    /**
     * Update token balances for all addresses in the system
     * This is a comprehensive update that ensures all balances are accurate
     */
    private async updateAllTokenBalances() {
        try {
            logger.info('Starting full token balance update for all addresses in the system');

            // Get all addresses with token balances from the database
            const addresses = await db.getAllAddressesWithTokenBalances();
            logger.info(`Found ${addresses.length} addresses with token balances in the database`);

            // Get known token addresses
            const knownTokenAddresses = await blockchain.getKnownTokenAddresses();
            logger.info(`Found ${knownTokenAddresses.length} known token addresses`);

            // Process addresses in batches to avoid overloading the blockchain provider
            const batchSize = 5;

            for (let i = 0; i < addresses.length; i += batchSize) {
                const batch = addresses.slice(i, i + batchSize);

                // Process each address in the batch
                await Promise.all(batch.map(async (address: string) => {
                    try {
                        // Get token balances directly from the blockchain
                        const balances = await blockchain.getAddressTokenBalances(address, knownTokenAddresses);

                        // Update the database with the latest balances
                        for (const balance of balances) {
                            try {
                                // Directly update the token balance in the database
                                await db.updateTokenBalance(
                                    address,
                                    balance.tokenAddress,
                                    balance.balance,
                                    balance.tokenType
                                );

                                logger.info(`Updated token balance for ${address} and token ${balance.tokenAddress}: ${balance.balance}`);
                            } catch (error) {
                                logger.error(`Error updating token balance for ${address} and token ${balance.tokenAddress}:`, error);
                            }
                        }
                    } catch (error) {
                        logger.error(`Error updating token balances for ${address}:`, error);
                    }
                }));

                // Wait a bit between batches to avoid rate limiting
                if (i + batchSize < addresses.length) {
                    await new Promise(resolve => setTimeout(resolve, 1000));
                }

                // Log progress
                if (i % 50 === 0 && i > 0) {
                    logger.info(`Processed ${i}/${addresses.length} addresses (${Math.round(i / addresses.length * 100)}%)`);
                }
            }

            logger.info('Completed full token balance update for all addresses');
        } catch (error) {
            logger.error('Error updating all token balances:', error);
        }
    }

    private async processNFTMetadata(tokenAddress: string, tokenId: string) {
        try {
            // Check if we already have metadata for this token
            const existingMetadata = await db.getNFTMetadata(tokenAddress, tokenId);
            if (existingMetadata) {
                // We already have metadata for this token
                return;
            }

            // Get the token URI
            const tokenURI = await blockchain.getTokenURI(tokenAddress, tokenId);
            if (!tokenURI) {
                logger.warn(`Token URI not found for token ${tokenAddress} with ID ${tokenId}`);
                return;
            }

            // Get the token metadata
            const metadata = await blockchain.getTokenMetadata(tokenAddress, tokenId);
            if (!metadata) {
                logger.warn(`Metadata not found for token ${tokenAddress} with ID ${tokenId}`);
                return;
            }

            // Store the metadata
            await db.updateNFTMetadata(tokenAddress, tokenId, metadata);

            logger.info(`Processed metadata for token ${tokenAddress} with ID ${tokenId}`);
        } catch (error) {
            logger.error(`Error processing NFT metadata for token ${tokenAddress} with ID ${tokenId}:`, error);
        }
    }

    private async processTokenCollection(tokenAddress: string) {
        try {
            // Check if we already have collection info for this token
            const existingCollection = await db.getNFTCollection(tokenAddress);
            if (existingCollection) {
                // We already have collection info for this token
                return;
            }

            // Get the token type
            const tokenType = await blockchain.getTokenType(tokenAddress);
            if (!tokenType || tokenType === 'ERC20') {
                // We only care about NFT collections
                return;
            }

            // Get the token name and symbol
            const name = await blockchain.getTokenName(tokenAddress);
            const symbol = await blockchain.getTokenSymbol(tokenAddress);

            // Get the token total supply
            const totalSupply = await blockchain.getTokenTotalSupply(tokenAddress);

            // Store the collection info
            await db.updateNFTCollection(
                tokenAddress,
                name || 'Unknown',
                symbol || 'UNKNOWN',
                totalSupply ? totalSupply.toNumber() : undefined
            );

            logger.info(`Processed collection info for token ${tokenAddress}`);
        } catch (error) {
            logger.error(`Error processing token collection for token ${tokenAddress}:`, error);
        }
    }

    private async processNewContracts(fromBlock: number, toBlock: number) {
        try {
            logger.info(`Detecting new contracts from block ${fromBlock} to ${toBlock}`);

            // Use the blockchain service to detect new contracts
            const contracts = await blockchain.detectNewContracts(fromBlock, toBlock);

            // Store each contract in the database
            for (const contract of contracts) {
                try {
                    await db.storeContract(contract);
                    logger.info(`Stored contract ${contract.address} in the database`);

                    // If this is a token contract, update token information
                    if (contract.contractType && contract.contractType !== 'UNKNOWN') {
                        // Process token information
                        if (contract.contractType === 'ERC20') {
                            // For ERC20 tokens, update token balances
                            logger.info(`Processing ERC20 token ${contract.address}`);
                            // This would be handled by the token transfers processing
                        } else if (contract.contractType === 'ERC721' || contract.contractType === 'ERC1155') {
                            // For NFT tokens, update collection information
                            logger.info(`Processing NFT collection ${contract.address}`);
                            await this.processTokenCollection(contract.address);
                        }
                    }
                } catch (error) {
                    logger.error(`Error storing contract ${contract.address}:`, error);
                }
            }

            logger.info(`Processed ${contracts.length} new contracts from block ${fromBlock} to ${toBlock}`);
        } catch (error) {
            logger.error(`Error processing new contracts from block ${fromBlock} to ${toBlock}:`, error);
        }
    }

    stop() {
        this._isRunning = false;
        logger.info('Stopping indexer...');
    }
}

export const indexer = new Indexer();

import { indexer } from './services/indexer';
import { createApiService } from './services/api';
import { blockchain } from './services/blockchain';
import { db } from './services/database/index';
import { createLogger } from './utils/logger';
import { config } from './config';

const logger = createLogger('main');

process.on('uncaughtException', (error) => {
    logger.error('Uncaught Exception:', error);
    process.exit(1);
});

process.on('unhandledRejection', (reason, promise) => {
    logger.error('Unhandled Rejection at:', promise, 'reason:', reason);
    process.exit(1);
});

async function main() {
    try {
        logger.info('Starting Studio Blockchain Indexer...');
        logger.info(`RPC URLs: ${config.rpc.urls.join(', ')}`);
        logger.info(`Chain ID: ${config.rpc.chainId}`);
        logger.info(`Starting from block: ${config.indexer.startBlock}`);

        // Create and start the API server
        const api = createApiService(db, indexer, config.server.port);
        api.start();
        logger.info(`API server started on port ${config.server.port}`);

        // Start the indexer
        await indexer.start();
        logger.info('Indexer started successfully');

        // Handle shutdown gracefully
        const shutdown = async () => {
            logger.info('Shutting down...');

            // Stop the indexer
            indexer.stop();

            // Shutdown blockchain service (stop health check interval)
            if (typeof blockchain.shutdown === 'function') {
                blockchain.shutdown();
            }

            process.exit(0);
        };

        process.on('SIGINT', shutdown);
        process.on('SIGTERM', shutdown);

    } catch (error) {
        logger.error('Fatal error:', error);
        process.exit(1);
    }
}

main().catch((error) => {
    logger.error('Startup error:', error);
    process.exit(1);
});

