// SPDX-License-Identifier: BUSL-1.1

contract UniswapV3PoolDeployer is IUniswapV3PoolDeployer {
    struct Parameters {
        address factory;
        address token0;
        address token1;
        uint24 fee;
        int24 tickSpacing;
    }

    /// @inheritdoc IUniswapV3PoolDeployer
    Parameters public override parameters;

    /// @dev Deploys a pool with the given parameters by transiently setting the parameters storage slot and then
    /// clearing it after deploying the pool.
    /// @param factory The contract address of the Uniswap V3 factory
    /// @param token0 The first token of the pool by address sort order
    /// @param token1 The second token of the pool by address sort order
    /// @param fee The fee collected upon every swap in the pool, denominated in hundredths of a bip
    /// @param tickSpacing The spacing between usable ticks
    function deploy(
        address factory,
        address token0,
        address token1,
        uint24 fee,
        int24 tickSpacing
    ) internal returns (address pool) {
        parameters = Parameters({factory: factory, token0: token0, token1: token1, fee: fee, tickSpacing: tickSpacing});
        pool = address(new UniswapV3Pool{salt: keccak256(abi.encode(token0, token1, fee))}());
        delete parameters;
    }
}

// File: ..\v3-core\contracts\UniswapV3Factory.sol

// SPDX-License-Identifier: BUSL-1.1

/// @title Canonical Uniswap V3 factory
/// @notice Deploys Uniswap V3 pools and manages ownership and control over pool protocol fees
contract UniswapV3Factory is IUniswapV3Factory, UniswapV3PoolDeployer, NoDelegateCall {
    /// @inheritdoc IUniswapV3Factory
    address public override owner;

    /// @inheritdoc IUniswapV3Factory
    mapping(uint24 => int24) public override feeAmountTickSpacing;
    /// @inheritdoc IUniswapV3Factory
    mapping(address => mapping(address => mapping(uint24 => address))) public override getPool;

    constructor() {
        owner = msg.sender;
        emit OwnerChanged(address(0), msg.sender);

        feeAmountTickSpacing[500] = 10;
        emit FeeAmountEnabled(500, 10);
        feeAmountTickSpacing[3000] = 60;
        emit FeeAmountEnabled(3000, 60);
        feeAmountTickSpacing[10000] = 200;
        emit FeeAmountEnabled(10000, 200);
    }

    /// @inheritdoc IUniswapV3Factory
    function createPool(
        address tokenA,
        address tokenB,
        uint24 fee
    ) external override noDelegateCall returns (address pool) {
        require(tokenA != tokenB);
        (address token0, address token1) = tokenA < tokenB ? (tokenA, tokenB) : (tokenB, tokenA);
        require(token0 != address(0));
        int24 tickSpacing = feeAmountTickSpacing[fee];
        require(tickSpacing != 0);
        require(getPool[token0][token1][fee] == address(0));
        pool = deploy(address(this), token0, token1, fee, tickSpacing);
        getPool[token0][token1][fee] = pool;
        // populate mapping in the reverse direction, deliberate choice to avoid the cost of comparing addresses
        getPool[token1][token0][fee] = pool;
        emit PoolCreated(token0, token1, fee, tickSpacing, pool);
    }

    /// @inheritdoc IUniswapV3Factory
    function setOwner(address _owner) external override {
        require(msg.sender == owner);
        emit OwnerChanged(owner, _owner);
        owner = _owner;
    }

    /// @inheritdoc IUniswapV3Factory
    function enableFeeAmount(uint24 fee, int24 tickSpacing) public override {
        require(msg.sender == owner);
        require(fee < 1000000);
        // tick spacing is capped at 16384 to prevent the situation where tickSpacing is so large that
        // TickBitmap#nextInitializedTickWithinOneWord overflows int24 container from a valid tick
        // 16384 ticks represents a >5x price change with ticks of 1 bips
        require(tickSpacing > 0 && tickSpacing < 16384);
        require(feeAmountTickSpacing[fee] == 0);

        feeAmountTickSpacing[fee] = tickSpacing;
        emit FeeAmountEnabled(fee, tickSpacing);
    }
}

import { Request, Response } from 'express';
import express from 'express';
import { createLogger } from '../../utils/logger';
import { ApiService } from './core';
import { blockchain } from '../blockchain';

const logger = createLogger('api:blockchain');

/**
 * BlockchainApiService class that extends the ApiService class
 * This class handles blockchain-related endpoints
 */
export class BlockchainApiService extends ApiService {
    constructor(database: any, indexer: any, port: number, app?: express.Application) {
        super(database, indexer, port, app);
    }

    /**
     * Set up blockchain-related routes
     */
    protected setupRoutes(): void {
        // Call the parent setupRoutes method to set up the base routes
        if (this.isMainService) {
            super.setupRoutes();
        }

        // Get block by number
        this.app.get('/blocks/:number', this.getBlockByNumber.bind(this));

        // Get block by hash
        this.app.get('/blocks/hash/:hash', this.getBlockByHash.bind(this));

        // Get transaction receipt
        this.app.get('/transactions/:hash/receipt', this.getTransactionReceipt.bind(this));

        // Get pending transactions
        this.app.get('/transactions/pending', this.getPendingTransactions.bind(this));

        // Get latest transactions
        this.app.get('/transactions', this.getLatestTransactions.bind(this));

        // Filter logs
        this.app.post('/logs/filter', this.filterLogs.bind(this));

        // Get latest blocks
        this.app.get('/blocks', this.getLatestBlocks.bind(this));

        // Get transaction by hash
        this.app.get('/transactions/:hash', this.getTransactionByHash.bind(this));

        // Get transactions by address
        this.app.get('/address/:address/transactions', this.getTransactionsByAddress.bind(this));

        // Get address type
        this.app.get('/address/:address/type', this.getAddressType.bind(this));

        // Search endpoint
        this.app.get('/search', this.search.bind(this));

        // Detect new contracts
        this.app.get('/contracts/detect', this.detectNewContracts.bind(this));
    }

    /**
     * Get a block by its number
     */
    private async getBlockByNumber(req: Request, res: Response): Promise<void> {
        try {
            const blockNumber = parseInt(req.params.number);
            const block = await this.database.getBlock(blockNumber);
            if (!block) {
                res.status(404).json({ error: 'Block not found' });
                return;
            }

            const transactions = await this.database.getTransactionsByBlock(block.number);
            res.json({
                ...block,
                transactions
            });
        } catch (error: any) {
            logger.error('Error getting block:', error);
            res.status(500).json({ error: 'Failed to get block' });
        }
    }

    /**
     * Get a block by its hash
     */
    private async getBlockByHash(req: Request, res: Response): Promise<void> {
        try {
            const block = await this.database.getBlock(req.params.hash);
            if (!block) {
                res.status(404).json({ error: 'Block not found' });
                return;
            }

            const transactions = await this.database.getTransactionsByBlock(block.number);
            res.json({
                ...block,
                transactions
            });
        } catch (error: any) {
            logger.error('Error getting block by hash:', error);
            res.status(500).json({ error: 'Failed to get block' });
        }
    }

    /**
     * Get a transaction receipt
     */
    private async getTransactionReceipt(req: Request, res: Response): Promise<void> {
        try {
            const receipt = await blockchain.getTransactionReceipt(req.params.hash);
            if (!receipt) {
                res.status(404).json({ error: 'Transaction receipt not found' });
                return;
            }
            res.json(receipt);
        } catch (error: any) {
            logger.error('Error getting transaction receipt:', error);
            res.status(500).json({ error: 'Failed to get transaction receipt' });
        }
    }

    /**
     * Get pending transactions
     */
    private async getPendingTransactions(req: Request, res: Response): Promise<void> {
        try {
            const pendingTxs = await blockchain.getPendingTransactions();
            res.json(pendingTxs);
        } catch (error: any) {
            logger.error('Error getting pending transactions:', error);
            res.status(500).json({ error: 'Failed to get pending transactions' });
        }
    }

    /**
     * Get latest transactions
     */
    private async getLatestTransactions(req: Request, res: Response): Promise<void> {
        try {
            const limit = parseInt(req.query.limit as string) || 10;
            const offset = parseInt(req.query.offset as string) || 0;

            const transactions = await this.database.getLatestTransactions(limit, offset);
            res.json(transactions);
        } catch (error: any) {
            logger.error('Error getting latest transactions:', error);
            res.status(500).json({ error: 'Failed to get latest transactions' });
        }
    }

    /**
     * Filter logs
     */
    private async filterLogs(req: Request, res: Response): Promise<void> {
        try {
            const filter = req.body;

            // Validate filter
            if (!filter || (!filter.address && !filter.topics)) {
                res.status(400).json({ error: 'Invalid filter. Must provide address and/or topics.' });
                return;
            }

            const logs = await blockchain.getLogs(filter);
            res.json(logs);
        } catch (error: any) {
            logger.error('Error filtering logs:', error);
            res.status(500).json({ error: 'Failed to filter logs' });
        }
    }

    /**
     * Get latest blocks
     */
    private async getLatestBlocks(req: Request, res: Response): Promise<void> {
        try {
            const limit = parseInt(req.query.limit as string) || 10;
            const offset = parseInt(req.query.offset as string) || 0;

            const latestBlock = await this.database.getLatestBlock();
            if (!latestBlock) {
                res.json([]);
                return;
            }

            const blocks = [];
            for (let i = 0; i < limit && (latestBlock - i - offset) >= 0; i++) {
                const blockNumber = latestBlock - i - offset;
                const block = await this.database.getBlock(blockNumber);
                if (block) {
                    blocks.push(block);
                }
            }

            res.json(blocks);
        } catch (error: any) {
            logger.error('Error getting blocks:', error);
            res.status(500).json({ error: 'Failed to get blocks' });
        }
    }

    /**
     * Get a transaction by its hash
     */
    private async getTransactionByHash(req: Request, res: Response): Promise<void> {
        try {
            const transaction = await this.database.getTransaction(req.params.hash);
            if (!transaction) {
                res.status(404).json({ error: 'Transaction not found' });
                return;
            }
            res.json(transaction);
        } catch (error: any) {
            logger.error('Error getting transaction:', error);
            res.status(500).json({ error: 'Failed to get transaction' });
        }
    }

    /**
     * Get transactions by address
     */
    private async getTransactionsByAddress(req: Request, res: Response): Promise<void> {
        try {
            const limit = parseInt(req.query.limit as string) || 10;
            const offset = parseInt(req.query.offset as string) || 0;

            const transactions = await this.database.getTransactionsByAddress(
                req.params.address,
                limit,
                offset
            );

            res.json(transactions);
        } catch (error: any) {
            logger.error('Error getting address transactions:', error);
            res.status(500).json({ error: 'Failed to get transactions' });
        }
    }

    /**
     * Get address type
     */
    private async getAddressType(req: Request, res: Response): Promise<void> {
        try {
            const address = req.params.address;

            // Check if it's a contract
            const isContract = await blockchain.isContract(address);

            if (isContract) {
                // Determine token type
                const tokenType = await blockchain.getTokenType(address);

                if (tokenType) {
                    res.json({ 
                        address, 
                        type: 'contract', 
                        contractType: tokenType 
                    });
                } else {
                    res.json({ 
                        address, 
                        type: 'contract', 
                        contractType: 'unknown' 
                    });
                }
            } else {
                // It's a regular wallet address
                res.json({ 
                    address, 
                    type: 'wallet' 
                });
            }
        } catch (error: any) {
            logger.error('Error getting address type:', error);
            res.status(500).json({ error: 'Failed to get address type' });
        }
    }

    /**
     * Search for a block, transaction, or address
     */
    private async search(req: Request, res: Response): Promise<void> {
        try {
            const query = req.query.q as string;
            if (!query) {
                res.status(400).json({ error: 'Query parameter is required' });
                return;
            }

            // Check if it's a block number (all digits)
            if (/^\d+$/.test(query)) {
                const block = await this.database.getBlock(parseInt(query));
                if (block) {
                    res.json({ type: 'block', data: block });
                    return;
                }
            }

            // Check if it's a block hash or transaction hash (0x followed by 64 hex chars)
            if (/^0x[a-fA-F0-9]{64}$/.test(query)) {
                // Try as transaction hash first
                const tx = await this.database.getTransaction(query);
                if (tx) {
                    res.json({ type: 'transaction', data: tx });
                    return;
                }

                // Try as block hash
                const block = await this.database.getBlock(query);
                if (block) {
                    res.json({ type: 'block', data: block });
                    return;
                }
            }

            // Check if it's an address (0x followed by 40 hex chars)
            if (/^0x[a-fA-F0-9]{40}$/.test(query)) {
                // Check if it's a contract
                const isContract = await blockchain.isContract(query);

                // Get transactions for this address
                const transactions = await this.database.getTransactionsByAddress(query, 10, 0);

                if (isContract) {
                    // Determine token type
                    const tokenType = await blockchain.getTokenType(query);

                    if (tokenType) {
                        res.json({ 
                            type: 'address', 
                            addressType: 'contract',
                            contractType: tokenType,
                            data: { 
                                address: query, 
                                transactions 
                            } 
                        });
                    } else {
                        res.json({ 
                            type: 'address', 
                            addressType: 'contract',
                            contractType: 'unknown',
                            data: { 
                                address: query, 
                                transactions 
                            } 
                        });
                    }
                } else {
                    // It's a regular wallet address
                    res.json({ 
                        type: 'address', 
                        addressType: 'wallet',
                        data: { 
                            address: query, 
                            transactions 
                        } 
                    });
                }
                return;
            }

            res.status(404).json({ error: 'No results found' });
        } catch (error: any) {
            logger.error('Error searching:', error);
            res.status(500).json({ error: 'Search failed' });
        }
    }

    /**
     * Detect new contracts
     */
    private async detectNewContracts(req: Request, res: Response): Promise<void> {
        try {
            const fromBlock = parseInt(req.query.fromBlock as string) || 0;
            const toBlock = parseInt(req.query.toBlock as string) || await blockchain.getLatestBlockNumber();

            const contracts = await blockchain.detectNewContracts(fromBlock, toBlock);
            res.json(contracts);
        } catch (error: any) {
            logger.error('Error detecting new contracts:', error);
            res.status(500).json({ error: 'Failed to detect new contracts' });
        }
    }
}

import express from 'express';
import { IDatabase } from '../database/types';
import { createLogger } from '../../utils/logger';
import { blockchain } from '../blockchain';
import { formatResponse, handleError } from './utils';

const logger = createLogger('api:contracts');

/**
 * API service for contract-related endpoints
 */
export class ContractsApiService {
    private database: IDatabase;
    private indexer: any;
    private app: express.Application;

    constructor(database: IDatabase, indexer: any, port: number, app: express.Application) {
        this.database = database;
        this.indexer = indexer;
        this.app = app;

        this.setupRoutes();
    }

    private setupRoutes() {
        // Get contract details
        this.app.get('/contracts/:address', this.getContract.bind(this));

        // Get contracts by creator
        this.app.get('/address/:address/contracts', this.getContractsByCreator.bind(this));

        // Get token contracts
        this.app.get('/tokens', this.getTokenContracts.bind(this));

        // Get contract creation info
        this.app.get('/contracts/:address/creation', this.getContractCreationInfo.bind(this));

        // Get account balances (tokens and native)
        this.app.get('/account/:address/balances', this.getAccountBalances.bind(this));

        // Get address tokens
        this.app.get('/address/:address/tokens', this.getAddressTokens.bind(this));

        // NOTE: Removed duplicate token-transfers endpoint to avoid conflict with TokensApiService
        // The endpoint is already registered in TokensApiService

        // Get token information
        this.app.get('/tokens/:tokenAddress', this.getTokenInfo.bind(this));

        // Get token holders
        this.app.get('/tokens/:tokenAddress/holders', this.getTokenHolders.bind(this));

        // Contract verification endpoints
        this.app.post('/contracts/verify', this.verifyContract.bind(this));
        this.app.get('/contracts/:address/verified', this.isContractVerified.bind(this));
        this.app.get('/contracts/:address/abi', this.getContractABI.bind(this));
        this.app.get('/contracts/:address/source', this.getContractSource.bind(this));
        this.app.post('/contracts/:address/interact', this.interactWithContract.bind(this));
    }

    /**
     * Get contract details
     */
    private async getContract(req: express.Request, res: express.Response) {
        try {
            const address = req.params.address.toLowerCase();

            // Get contract from database
            const contract = await this.database.getContract(address);

            if (!contract) {
                // If not in database, check if it's a contract on-chain
                const isContract = await blockchain.isContract(address);

                if (!isContract) {
                    return formatResponse(res, { error: 'Not a contract address' }, 404);
                }

                // Get contract creation info
                const creationInfo = await blockchain.getContractCreationInfo(address);

                if (!creationInfo) {
                    return formatResponse(res, { error: 'Contract creation info not found' }, 404);
                }

                // Get contract type and details
                const tokenType = await blockchain.getTokenType(address);
                const nameResult = tokenType ? await blockchain.getTokenName(address) : null;
                const symbolResult = tokenType ? await blockchain.getTokenSymbol(address) : null;
                let decimalsResult;
                let totalSupply;

                if (tokenType === 'ERC20') {
                    decimalsResult = await blockchain.getTokenDecimals(address);
                    totalSupply = await blockchain.getTokenTotalSupply(address);
                }

                // Convert null values to undefined
                const name = nameResult || undefined;
                const symbol = symbolResult || undefined;
                const decimals = decimalsResult || undefined;

                // Create contract data
                const contractData: import('../blockchain/types').ContractData = {
                    address,
                    creatorAddress: creationInfo.creator,
                    blockNumber: creationInfo.blockNumber,
                    timestamp: creationInfo.timestamp,
                    transactionHash: creationInfo.transactionHash,
                    contractType: (tokenType as 'ERC20' | 'ERC721' | 'ERC1155' | 'UNKNOWN') || 'UNKNOWN',
                    name,
                    symbol,
                    decimals,
                    totalSupply: totalSupply ? totalSupply.toString() : undefined
                };

                // Store contract in database
                await this.database.storeContract(contractData);

                return formatResponse(res, contractData);
            }

            return formatResponse(res, contract);
        } catch (error) {
            return handleError(res, error, 'Error getting contract');
        }
    }

    /**
     * Get contracts created by an address
     */
    private async getContractsByCreator(req: express.Request, res: express.Response) {
        try {
            const address = req.params.address.toLowerCase();
            const limit = parseInt(req.query.limit as string) || 10;
            const offset = parseInt(req.query.offset as string) || 0;

            const contracts = await this.database.getContractsByCreator(address, limit, offset);

            return formatResponse(res, contracts);
        } catch (error) {
            return handleError(res, error, 'Error getting contracts by creator');
        }
    }

    /**
     * Get token contracts
     */
    private async getTokenContracts(req: express.Request, res: express.Response) {
        try {
            const tokenType = req.query.type as 'ERC20' | 'ERC721' | 'ERC1155' | undefined;
            const limit = parseInt(req.query.limit as string) || 10;
            const offset = parseInt(req.query.offset as string) || 0;

            const contracts = await this.database.getTokenContracts(tokenType, limit, offset);

            return formatResponse(res, contracts);
        } catch (error) {
            return handleError(res, error, 'Error getting token contracts');
        }
    }

    /**
     * Get contract creation info
     */
    private async getContractCreationInfo(req: express.Request, res: express.Response) {
        try {
            const address = req.params.address.toLowerCase();

            // Check if it's a contract
            const isContract = await blockchain.isContract(address);

            if (!isContract) {
                return formatResponse(res, { error: 'Not a contract address' }, 404);
            }

            // Get contract creation info
            const creationInfo = await blockchain.getContractCreationInfo(address);

            if (!creationInfo) {
                return formatResponse(res, { error: 'Contract creation info not found' }, 404);
            }

            return formatResponse(res, creationInfo);
        } catch (error) {
            return handleError(res, error, 'Error getting contract creation info');
        }
    }

    /**
     * Get account balances (tokens and native)
     */
    private async getAccountBalances(req: express.Request, res: express.Response) {
        try {
            const address = req.params.address.toLowerCase();

            // Get native balance
            const nativeBalance = await blockchain.getBalance(address);

            // Get token balances
            const tokenBalances = await this.database.getAddressTokenBalances(address);

            // Format token balances
            const formattedTokens = await Promise.all(
                tokenBalances.map(async (balance) => {
                    try {
                        const tokenAddress = balance.tokenAddress;
                        const tokenType = balance.tokenType;

                        // Get token details
                        const name = await blockchain.getTokenName(tokenAddress);
                        const symbol = await blockchain.getTokenSymbol(tokenAddress);
                        let decimals = 18; // Default to 18 decimals

                        if (tokenType === 'ERC20') {
                            const tokenDecimals = await blockchain.getTokenDecimals(tokenAddress);
                            if (tokenDecimals !== null) {
                                decimals = tokenDecimals;
                            }
                        }

                        // Convert balance from wei to token units
                        const balanceValue = parseFloat(balance.balance) / Math.pow(10, decimals);

                        return {
                            contractAddress: tokenAddress,
                            symbol: symbol || 'UNKNOWN',
                            name: name || 'Unknown Token',
                            balance: balanceValue,
                            decimals,
                            type: tokenType
                        };
                    } catch (error) {
                        logger.error(`Error formatting token balance for ${balance.tokenAddress}:`, error);
                        return null;
                    }
                })
            );

            // Filter out null values
            const tokens = formattedTokens.filter(token => token !== null);

            // Convert native balance from wei to ether
            const nativeValue = parseFloat(nativeBalance.toString()) / 1e18;

            return formatResponse(res, {
                native: nativeValue,
                tokens
            });
        } catch (error) {
            return handleError(res, error, 'Error getting account balances');
        }
    }

    /**
     * Get address tokens
     */
    private async getAddressTokens(req: express.Request, res: express.Response) {
        try {
            const address = req.params.address.toLowerCase();

            // Get token balances
            const tokenBalances = await this.database.getAddressTokenBalances(address);

            // Format token balances
            const formattedTokens = await Promise.all(
                tokenBalances.map(async (balance) => {
                    try {
                        const tokenAddress = balance.tokenAddress;
                        const tokenType = balance.tokenType;

                        // Get token details
                        const name = await blockchain.getTokenName(tokenAddress);
                        const symbol = await blockchain.getTokenSymbol(tokenAddress);
                        let decimals = 18; // Default to 18 decimals

                        if (tokenType === 'ERC20') {
                            const tokenDecimals = await blockchain.getTokenDecimals(tokenAddress);
                            if (tokenDecimals !== null) {
                                decimals = tokenDecimals;
                            }
                        }

                        // Convert balance from wei to token units
                        const balanceValue = parseFloat(balance.balance) / Math.pow(10, decimals);

                        return {
                            contractAddress: tokenAddress,
                            symbol: symbol || 'UNKNOWN',
                            name: name || 'Unknown Token',
                            balance: balanceValue,
                            decimals,
                            type: tokenType
                        };
                    } catch (error) {
                        logger.error(`Error formatting token balance for ${balance.tokenAddress}:`, error);
                        return null;
                    }
                })
            );

            // Filter out null values
            const tokens = formattedTokens.filter(token => token !== null);

            return formatResponse(res, tokens);
        } catch (error) {
            return handleError(res, error, 'Error getting address tokens');
        }
    }

    /**
     * Get token transfers for an address
     * NOTE: This method is kept for reference but not registered as an endpoint
     * to avoid conflict with TokensApiService
     */
    private async getAddressTokenTransfers(req: express.Request, res: express.Response) {
        try {
            const address = req.params.address.toLowerCase();
            const limit = parseInt(req.query.limit as string) || 10;
            const offset = parseInt(req.query.offset as string) || 0;

            // Get token transfers
            const transfers = await this.database.getAddressTokenTransfers(address, {
                limit,
                offset
            });

            // Format token transfers
            const formattedTransfers = await Promise.all(
                transfers.map(async (transfer) => {
                    try {
                        const tokenAddress = transfer.tokenAddress;

                        // Get token details
                        const name = await blockchain.getTokenName(tokenAddress);
                        const symbol = await blockchain.getTokenSymbol(tokenAddress);
                        let decimals = 18; // Default to 18 decimals

                        if (transfer.tokenType === 'ERC20') {
                            const tokenDecimals = await blockchain.getTokenDecimals(tokenAddress);
                            if (tokenDecimals !== null) {
                                decimals = tokenDecimals;
                            }
                        }

                        return {
                            hash: transfer.transactionHash,
                            blockNumber: transfer.blockNumber,
                            timestamp: transfer.timestamp,
                            from: transfer.fromAddress,
                            to: transfer.toAddress,
                            tokenAddress,
                            tokenSymbol: symbol || 'UNKNOWN',
                            tokenName: name || 'Unknown Token',
                            value: transfer.value,
                            decimals
                        };
                    } catch (error) {
                        logger.error(`Error formatting token transfer for ${transfer.transactionHash}:`, error);
                        return null;
                    }
                })
            );

            // Filter out null values
            const result = formattedTransfers.filter(transfer => transfer !== null);

            return formatResponse(res, result);
        } catch (error) {
            return handleError(res, error, 'Error getting token transfers');
        }
    }

    /**
     * Get token information
     */
    private async getTokenInfo(req: express.Request, res: express.Response) {
