    // Transaction decoder methods
    decodeTransactionData = transactionDecoder.decodeTransactionData.bind(transactionDecoder);
    formatDecodedData = transactionDecoder.formatDecodedData.bind(transactionDecoder);
    getTransactionDescription = transactionDecoder.getTransactionDescription.bind(transactionDecoder);
}

