import { enhancedImportHandler } from '../verification/enhanced-import-handler';
import { MultiFileVerificationOptions } from '../verification/verification-types';

const logger = createLogger('api:verification');

/**
 * API service for contract verification endpoints
 */
export class VerificationApiService {
    private database: IDatabase;
    private app: express.Application;

    constructor(database: IDatabase, app: express.Application) {
        this.database = database;
        this.app = app;

        this.setupRoutes();
    }

    private setupRoutes() {
        // Get contract verification details
        this.app.get('/api/v2/contracts/:address/verification', this.getContractVerificationDetails.bind(this));

        // Verify a contract
        this.app.post('/api/v2/contracts/verify', this.verifyContract.bind(this));

        // Verify a multi-file contract
        this.app.post('/api/v2/contracts/verify-multi', this.verifyMultiFileContract.bind(this));

        // Flatten a multi-file contract
        this.app.post('/api/v2/contracts/flatten', this.flattenContract.bind(this));

        // Get supported compiler versions
        this.app.get('/api/v2/verification/compiler-versions', this.getCompilerVersions.bind(this));

        // Get supported EVM versions
        this.app.get('/api/v2/verification/evm-versions', this.getEvmVersions.bind(this));
    }

    /**
     * Extract license from source code
     * @param sourceCode The source code
     * @returns The license
     */
    private extractLicense(sourceCode: string): string | null {
        // Look for SPDX license identifier
        const spdxMatch = sourceCode.match(/SPDX-License-Identifier:\s*([^\n\r]+)/);
        if (spdxMatch && spdxMatch[1]) {
            return spdxMatch[1].trim();
        }

        // Look for other common license patterns
        const licensePatterns = [
            /\s*@license\s+([^\n\r]+)/i,
            /\s*@title\s+.*\n\s*@license\s+([^\n\r]+)/i,
            /\s*License:\s+([^\n\r]+)/i,
            /\s*Licensed under\s+([^\n\r]+)/i
        ];

        for (const pattern of licensePatterns) {
            const match = sourceCode.match(pattern);
            if (match && match[1]) {
                return match[1].trim();
            }
        }

        return null;
    }

    /**
     * Get contract verification details
     */
    private async getContractVerificationDetails(req: express.Request, res: express.Response) {
        try {
            const address = req.params.address.toLowerCase();

            // Check if it's a contract
            const isContract = await blockchain.isContract(address);

            if (!isContract) {
                return formatResponse(res, { error: 'Not a contract address' }, 404);
            }

            // Get the contract from the database
            const contract = await this.database.getContract(address);

            if (!contract) {
