                        if (output.contracts[file][contractName]) {
                            contract = output.contracts[file][contractName];
                            contractFile = file;
                            break;
                        }
                    }
                }
            }

            if (!contract) {
                throw new Error(`Contract ${contractName} not found in any of the source files`);
            }

