            logger.error('Error getting compiler versions:', error);
            return handleError(res, error, 'Error getting compiler versions');
        }
    }

    /**
     * Get supported EVM versions
     */
    private async getEvmVersions(req: express.Request, res: express.Response) {
        try {
            const evmVersions = verificationService.getSupportedEvmVersions();

            return formatResponse(res, {
                success: true,
                evmVersions
            });
        } catch (error) {
            logger.error('Error getting EVM versions:', error);
            return handleError(res, error, 'Error getting EVM versions');
        }
    }
}


